INSERT INTO `tbl_web_kamus` (`kamus_id`, `kamus_istilah`, `kamus_detail`) VALUES
(16, 'Aboritifasien', 'Menyebabkan keguguran atau zat yang mengimbas keguguran.'),
(17, 'Abortus', 'Fetus yang mati atau tak mampu hidup.'),
(18, 'Abrakia', 'Ketiadaan kaki depan bawaan.'),
(19, 'Abrakiosefalia', 'Suatu kelainan selama perkembangan sehingga tidak terbentuk kepala atau kaki depan.'),
(20, 'Abrasi', 'Suatu luka yang disebabkan oleh gosokan atau garutan pada kulit atau selaput lendir.'),
(21, 'Abrasif', 'Menyebabkan abrasi atau suatu zat yang menghasilkan abrasi.'),
(22, 'Abses', 'Suatu kumpulan nanah terlokasi di suatu ruangan terbentuk karena disintegrasi jaringan. Kebanyakan abses terbentuk karena invasi jaringan oleh bakteri, tetapi kadang-kadang disebabkan oleh fungi atau protozoa atau bahkan cacing dan beberapa steril. Pengaruhnya ditentukan oleh letaknya dan tekanan yang dilakukan pada organ di dekatnya, dan derajat toksemia yang ditimbulkan oleh kandungan bakterinya dan jumlah jaringan yang dirusak. Suatu abses yang aktif menunjukkan sindrom salah satu dari nyeri local, anoreksia dan demam, dan leukositosis.'),
(23, 'Achillobursitis', 'Radang bursa di sekitar tendo gastroknemius.'),
(24, 'Achyla', 'Suatu genus fungi yang menyebabkan penyakit pada ikan yang di pelihara oleh ahli budidaya ikan dan ahli ikan akuarium.'),
(25, 'Aciner', 'Mengenai atau mempengaruhi asinus atau asini.'),
(26, 'Actinocleidus', 'Suatu genus cacing daun yang menjadi parasit pada ikan.'),
(27, 'Adaktilia', 'Ketiadaan jejari sejak lahir.'),
(28, 'Adenalgia ', 'Nyeri di kelenjar.'),
(29, 'Adenastenia', 'Berkurangnya aktivitas kelenjar.'),
(30, 'Adenektomi', 'Pemotongan kelenjar.'),
(31, 'Adeknektopia', 'Pergeseran kelenjar.'),
(32, 'Adenitis', 'Peradangan kelenjar.'),
(33, 'Adenodinia', 'Nyeri di kelenjar.'),
(34, 'Adenoid', 'Menyerupai suatu kelenjar atau tonsil faringeal yang membesar.'),
(35, 'Adenokel', 'Tumor adenomatus sistik.'),
(36, 'Adenokondroma', 'Suatu tumor yang mengandung unsur-unsur kelenjar dan kartilago.'),
(37, 'Adenolimfitis', 'Limfadenitis atau peradangan nodus limfe.'),
(38, 'Adenolimfoma', 'Suatu tumor kelenjar ludah sistik yang mengandung jaringan epitel dan jaringan limfoid.'),
(39, 'Adenolipoma', 'Suatu tumor yang tersusun atas unsur-unsur kelenjar dan jaringan lemak.'),
(40, 'Adenoma kortikotrof', 'Tumor fungsional dari bagian intermedier atau bagian distal kelenjar pituitaria, pada anjing sindroma kelebihan kortisol. Penyakit Cushing.'),
(41, 'Adenoma ', 'Tumor benigna yang sel-selnya membentuk struktur kelenjar yang dapat dikenali atau yang sel-selnya berasal dari epitel kelenjar.'),
(42, 'Adenomalasia', 'Kelunakan yang berlebihan suatu kelenjar.'),
(43, 'Adenomioma', 'Suatu tumor yang tersusun atas endometrium dan jaringan otot, ditemukan dalam uterus, atau lebih sering lagi, di ligamentum uterus.'),
(44, 'Adenopati', 'Pembesaran kelenjar, terutama nodus limfa tikus.'),
(45, 'Adenophitelioma', 'Suatu tumor yang tersusun atas unsur-unsur kelenjar dan epitel.'),
(46, 'Adenoselulitis', 'Radang suatu kelenjar dan jaringan selular di sekitarnya.'),
(47, 'Adenosis ', 'Suatu penyakit kelenjar atau perkembangan abnormal kelenjar.'),
(48, 'Adenosistoma', 'Adenoma yang di dalamnya ada pembentukan sista.'),
(49, 'Adenosklerosis', 'Pengerasan suatu kelenjar.'),
(50, 'Adenotomi', 'Anatomi, penyayatan atau pembedahan kelenjar.'),
(51, 'Adermia', 'Cacat bawaan tanpa kulit.'),
(52, 'Adipik', 'Mengenai lemak.'),
(53, 'Adipofibroma', 'Suatu tumor fibrus dengan unsur-unsur lemak.'),
(54, 'Adipogenik', 'Mengasilkan lemak.'),
(55, 'Adipokinin', 'Suatu faktor dari kelenjar pituitaria anterior yang mempercepat mobilisasi lemak yang tersimpan.'),
(56, 'Adiposis', 'Suatu keadaan, menonjol karena deposit atau degenerasi jaringan lemak.'),
(57, 'Adiposit', 'Sel yang khusus untuk penyimpanan lemak, lemak disimpan dalam vesikula sitoplasmik yang besar.'),
(58, 'Adiposuria', 'Adanya lemak dalam urine.'),
(59, 'Adipsia', 'Ketiadaan rasa haus, penolakan minuman yang abnormal.'),
(60, 'Adonsia', 'Ketiadaan gigi.'),
(61, 'Babesiosis', 'Termasuk penyakit-penyakit yang disebabkan oleh protozoa Babesia spp. Dan ditularkan oleh caplak pengisap darah. Secara klinik mereka semua menciri dengan demam dan hemolysis intravaskuler bermanifestasi dengan suatu sindrom anemi, hemoglobinuria dan kuning. Disebut juga demam caplak, demam texas, demam urine merah.'),
(62, 'Bafikot', 'Lapisan yang berwarna abu-abu kemerahan yang terdiri dari sel darah putih (SDP) dan platelet yang dapat diamati di atas sel darah merah yang telah di sentrifus.'),
(63, 'Bakteremi', 'Adanya bakteri yang bersifat sementara dalam darah. Keadaan ini tidak ditunjukkan oleh setiap gejala klinik, tetapi biasanya diikuti oleh perkembangan  infeksi embolik seperti artritis, meningitis, endocarditis dan abses hati dan paru.'),
(64, 'Bakterid', 'Suatu erupsi kulit yang disebabkan oleh infeksi bakteri di tempat lain di tubuh.'),
(65, 'Bakterin ', 'Vaksin yang terdiri dari bakteri yang dibunuh.'),
(66, 'Bakteriolisin', 'Suatu antibodi yang meliliskan sel-sel bakteri.'),
(67, 'Bakteriolisis', 'Perusakan atau pelarutan bakteri.'),
(68, 'Bakteriospermia', 'Adanya bakteri dalam semen.'),
(69, 'Bakteriostasis', 'Suatu keadaan yang menunjukkan bahwa pertumbuhan atau multiplikasi bakteri dihambat tanpa bakteri menjadi terbunuh.'),
(70, 'Bakteroid', 'Menyerupai suatu bacterium atau suatu bakterium yang secara struktural termodifikasi.'),
(71, 'Bakteriotastik', 'Menahan pertumbuhan atau multiplikasi bakteri, juga suatu agen yang beraksi demikian.'),
(72, 'Bakteriuri', 'Bakteri dalam urine.'),
(73, 'Bakteropsonin', 'Suatu opsonin yang beraksi pada bakteri.'),
(74, 'Balanik', 'Berkaitan dengan glans penis atau glans klitoridis.'),
(75, 'Balanitis', 'Inflamasi glans penis.'),
(76, 'Balanopostektomi', 'Pembuangan dengan pembedahan penis dan prepusium.'),
(77, 'Balanopostitis', 'Inflamasi penis dan prepusium.'),
(78, 'Balanoprepusial', 'Berkaitan dengan glans penis dan prepusium.'),
(79, 'Balanoragia', 'Balanitis dengan pengeluaran bebas nanah.'),
(80, 'Balantidiasis', 'Infeksi protozoa genus Balantidium. Disentri babi pada suatu waktu dikira merupakan suatu bentuk balantidiasis yang disebabkan oleh B. coli. Sekarang, infeksi dikenal umum dan kadang-kadang menyebabkan suatu enteritis hebat pada babi dan anjing. Suatu zoonosis dan infeksi pada orang menyebabkan kolitis.'),
(81, 'Balantidium', 'Suatu genus protozoa bersilia, termasuk banyak spesies yang ditemukan di usus vertebrata dan avertebrata. Lihat juga BALANTIDIASIS.'),
(82, 'Balneoterapi', 'Penggunaan mandi dalam pengobatan penyakit.'),
(83, 'Bangkong', 'Katak puru, katak yang berkulit kasar, berbintik-bintik, berwarna kehitam-hitaman, terkadang berwarna seperti tanah.'),
(84, 'Barbiton', 'Salah satu barbiturat, merupakan suatu hipnotik dan sedatif yang beraksi lama. Disebut juga barbital. Sebagian besar diganti oleh fenobarbiton.'),
(85, 'Barbiturat', 'Setiap kelompok senyawa organik yang berasal dari asam barbiturat. Ada sejumlah barbiturat. Mereka semua mendepres sistem saraf dan digunakan untuk menginduksi apati dan tidur, dan dalam dosis tinggi, sebagai anestetik. Mereka bervariasi dalam efek sedatifnya, dalam lama efektivitasnya dan dalam toksisitasnya.'),
(86, 'Batriatriks', 'Suatu bidang ilmu kedokteran yang mencakup kajian tentang kelebihan berat, pencegahan dan pengobatannya.'),
(87, 'Barofilik', 'Tumbuh paling baik di bawah tekanan atmosferik tinggi, istilah untuk bakteria.'),
(88, 'Baroreseptor', 'Suatu ujung saraf sensori yang distimulasi oleh perubahan-perubahan tekanan, seperti yang pada dinding pembuluh darah.'),
(89, 'Barosinusitis ', 'Suatu kompleks gejala pada manusia yang disebabkan oleh perbedaan-perbedaan dalam tekanan atmosferik lingkungan dan tekanan udara di sinus paranasal.'),
(90, 'Barotaksis', 'Stimulasi zat yang hidup oleh perubahan tekanan atmosferik.'),
(91, 'Bartholin', 'Kelenjar vestibuler utama, dua kelenjar kecil, satu di masing-masing dinding vestibulum vagina sapi, kucing dan kadang-kadang domba yang mensekresi mukus, salurannya terbuka pada setiap sisi lubang uretra. Mereka mensekresi mukus yang menyediakan lubrikasi (pelicin) untuk koitus dan untuk lewatnya fetus saat kelahiran. Bila sistik pada sapi tampak menembus mukosa dan kira-kira panjangnya 3 cm dan lebarnya 1,5 cm. Mereka homolog kelenjar bulbouretra pada hewan jantan. Disebut juga kelenjar Tiedmann atau Duverney, kelenjar vulvovagina.'),
(92, 'Bartholin', 'Saluran bawah lidah utama. Mereka mengalirkan kelenjar bawah lidah monostomatik yang membuka karunkula bawah lidah. Mereka tak ada pada kuda.'),
(93, 'Bartolinitis', 'Inflamasi kelenjar Bartholin.'),
(94, 'Bartonella', 'Suatu genus famili Bartonellacene. B. bacilliformis adalah penyebab demam oroya atau Carrion pada manusia dan kadang-kadang anjing di Amerika Serikat.'),
(95, 'Bartonellaceae ', 'Suatu famili dalam ordo Rickettsiales, terdapat sebagai parasit patogenik di atau pada eritrosit manusia dan hewan lain. Lihat Bartonella.'),
(96, 'Bartonelomia ', 'Adanya organisme genus Bartonella di darah.'),
(97, 'Basidiobolus', 'Suatu genus fungi kelompok Phycemycetes.'),
(98, 'Basidiospero', 'Suatu fungi yang lebih tinggi tertentu, terbentuk pada suatu basidium sesudah kariogami dan meiosis.'),
(99, 'Basidium', 'Organ fungi lirpenthung yang membawa basiospora.'),
(100, 'Basilema', 'Selaput dasar.'),
(101, 'Basilemia ', 'Adanya basili dalam darah.'),
(102, 'Basilin', 'Suatu zat antibiotik yang diisolasi dari galur-galur Basillus substilis, sangat aktif pada bakteri gram-positif dan gram-negatif.'),
(103, 'Basiloma', 'Suatu karsinoma sel basal.'),
(104, 'Basilosis', 'Infeksi dengan basili.'),
(105, 'Basiluria', 'Basili dalam urine.'),
(106, 'Basilus', 'Suatu organisme dari genus Bacillus atau setiap bakteri yang berbentuk batang.'),
(107, 'Basitrasin', 'Suatu zat antibakterial yang dihasilkan oleh kelompok likeniformis Bacillus subtilis, yang ditemukan di suatu luka yang berkontaminasi, dan diberi nama menurut pasien, Margaret Tracy, berguna dalam suatu kisaran luas infeksi, digunakan secara olesan atau diberikan intramuskuler.'),
(108, 'Basoeritrosit', 'Suatu eritrosit yang mengandung granula basofil.'),
(109, 'Basofil', 'Setiap struktur, sel, unsur jaringan yang mudah tercat dengan zat warna basis, suatu leukosit granuler dan nukleus yang bentuknya tak teratur secara partial mengerut menjadi dua lobus (cuping), dan dengan sitoplasma yang mengandung granula bermacam-macam ukuran, kasar berwarna hitam-kebiruan atau suatu sel beta adenohipofisis, biasa disebut basofilik.'),
(110, 'Basofilisme', 'Kenaikan abnormal sel-sel basofilik.'),
(111, 'Basoplasma', 'Sitoplasma yang tercat dengan warna basis.'),
(112, 'Batipnea', 'Bernapas dalam.'),
(113, 'Batisanestesi', 'Hilangnya sensibilitas sangat.'),
(114, 'Beladona', 'Atropa beladona (deadly nightshade), suatu tumbuh-tumbuhan yang merupakan sumber bermacam-macam alkaloid, misalnya atropin, hiosiamin dan lain-lain.'),
(115, 'Benedia', 'Suatu genus kelas Trematoda, mereka merupakan parasit cacing daun ikan akuarium dan ikan yang dipiara.'),
(116, 'Benedict', 'Suatu larutan kimia yang digunakan untuk menentukan adanya glukosa dan zat-zat yang mereduksi lainnya dalam urine, disebut juga reagen Benedict.'),
(117, 'Benigna', 'Tidak ganas, tidak berulang (kambuh), baik untuk kesembuhan.'),
(118, 'Benzatin', 'Suatu asam amonium yang jika digabungkan dengan senyawa pensilin memperpanjang tinggalnya di jaringan.'),
(119, 'Benzetonium', 'Suatu senyawa amonium kuarterner yang digunakan sebagai suatu anti-infektif lokal dan sebagai suatu deterjen dan disinfektan.'),
(120, 'Benzfetamin', 'Suatu amin simpatomimetik, yang digunakan sebagai suatu anoreksian dalam bentuk garam hidroklarida.'),
(121, 'Benzidin', 'Suatu senyawa yang digunakan sebagai suatu uji untuk penemuan darah (uji benzidin).'),
(122, 'Benzodiazepin', 'Suatu kelompok obat yang mempunyai struktur molekul sama. Dua obat dari kelompok ini yang punya penggunaan signifikan di kedokteran veteriner adalah diazepam (valium) dan klordiazepoksid (librium).'),
(123, 'Cacar', 'Penyakit virus pada manusia dan primata tersifat oleh demam, ruan dan pembentukan keropeng. Disebut juga smallpox.'),
(124, 'Cacing', 'Dalam konteks kedokteran hewan berarti endoparasit helmin.'),
(125, 'Caecilian', 'Jenis amfibia lircacing, tidak mempunyai kaki, termasuk dalam ordo Apoda.'),
(126, 'Caliciviridae', 'Famili virus kira-kira berdiameter 35 nm, dengan kapsid yang membawa 32 lekukan sirkuler lir-mangkuk yang dangkal dan genom pita RNA tunggal kira-kira 8 kilobase. Ada satu genus, Calcivirus dan virus yang termasuk di dalamnya kalsivirus kucing, virus eksantema vesikuler pada babi dan virus San Miguel pada singa laut.'),
(127, 'Callicophoron ', 'Cacing daun lambung ruminansia. Termasuk di dalamnya spesies C. calicophorum, C. cauliorchis, C.ijimai dan C.raja.'),
(128, 'Calliphora', 'Suatu genus lalat termasuk C. augur, C. austalis, C. erythrocephala, C. fallax, C. hilli, C. novica, C. stygia dan C. vomitora. Mereka dapat menginisiasi serangan lalat pada domba tetapi mereka menjadi lebih penting daripada domba yang telah terinfeksi.'),
(129, 'Calliphoridae', 'Famili lalat yang terdiri dari beberapa lalat hijau yang penting, termasuk Calliphora, Chrysomyla, Lucilia, Callitroga dan Phormia spp.'),
(130, 'Callitroga', 'Suatu genus cacing kait yang meliputi C. Americana, C. hominivorax dan C. macellaria (disebut juga juga Cochliomyia hominivorax dan C. macellaria). Lihat pada mijasis KUTENUS cacing kait.'),
(131, 'Camallanus', 'Nematoda kura-kura air tawar.'),
(132, 'Camelostrongylus', 'Suatu nama genus cacing usus dari famili Trichostrongylidae.'),
(133, 'Camelidae ', 'Salah satu dari enam famili ruminisia dalam ordo ARTIODACTYLA.'),
(134, 'Cancang', 'Mengikat hewan pada kepalanya atau lehernya sehingga dia hanya dapat merumput tetapi tidak dapat lari.'),
(135, 'Canidae', 'Suatu famili yang terdiri atas 14 genus, dua yang paling umum ialah anjing (Canis spp) dan rubah (Vulpes spp).'),
(136, 'Cardiongiografi', 'Teknologi pengamatan radiologi pada jantung dan pembuluh darah. Ini membutuhkan perkakas dan teknik canggih karena cepatnya perjalanan material radiopak yang diinjeksikan melalui jantung. Disebut juga angiokardiografi.'),
(137, 'Cardivirus ', 'Salah satu genus virus famili Picornaviridae. Termasuk virus ensefalomiokarditis yang menyebabkan penyakit pada rodensia, babi dan manusia.'),
(138, 'Carmyerius', 'Suatu genus cacing daun dalam rumen yang ditemukan di Asia, anggota famili Paramphistomatidae.'),
(139, 'Carpoglyphus', 'Suatu genus insekta famili Acaridae.'),
(140, 'Caryophyllaeus', 'Suatu genus dari famili Oestridae.'),
(141, 'Cephalopsis', 'Anggota genus lalat dari famili Oestridae.'),
(142, 'Cephalotricum', 'Suatu genus fungus yang telah dilaporkan dapat menginfeksi jaringan subkutan anjing dan kucing.'),
(143, 'Ceratoppogonidae', 'Famili lalat penggigit, paling penting genus CULICOIDES.'),
(144, 'Ceratostomella', 'Fungus yang tumbuh pada pakan hewan yang disimpan.'),
(145, 'Cervidae', 'Famili dari ordo Artiodactyla atau ruminisia, yang terdiri dari jenis rusa termasuk elk dan moose.'),
(146, 'Cervus ', 'Suatu genus rusa dari famili Cervidae.'),
(147, 'Chabertia ', 'Suatu genus nematoda pada superfamili Strongyloidea.'),
(148, 'Chagas', 'TRIPANOSOMIASIS Amerika. Disebut juga penyakit Chagas-Cruz.'),
(149, 'Cheilospirura', 'Suatu genus nematoda famili Acuariidae.'),
(150, 'Chilodenella', 'Suatu genus siliata pada filum Ciliophora yang terdapat pada ikan dan amfibia.'),
(151, 'Chilomitus', 'Protozoa nirpatogenik pada ordo Trichomonadida.'),
(152, 'Chilopoda', 'Suatu klas dari filum Arthropoda yang mencakup sentipedes.'),
(153, 'Chirodiscoides', 'Tungau kulit subrodo Sarcoptiformes.'),
(154, 'Chirodropsidae', 'Famili ubur-ubur dalam ordo Cubomedusae. Mencakup tawon laut penyengat dan ubur-ubur kotak.'),
(155, 'Chirodropus', 'Salah satu dari empat genus pada famili Chirodropsidae, termasuk ubur-ubur kotak beracun.'),
(156, 'Chiropsalmus', 'Salah satu dari empat genus yang menyusun famili Chirodropsidae beracun, atau ubur-ubur kotak beracun.'),
(157, 'Chloriopsoroptes', 'Suatu genus tungau parasit famili Psoroptidae.'),
(158, 'Choniangium ', 'Suatu ganus cacing gilik famili Strongylidae.'),
(159, 'Chorioptes', 'Suatu genus tungau kudis famili Psoroptidae.'),
(160, 'Chrysomya', 'Suatu genus lalat famili Calliphoridae.'),
(161, 'Chrysosporium', 'Organisme tanah saprofitik, yang jika terhirup, menyebabkan peradangan granulomatosa pada paru (adiaspiromikosis). Semula Emmonsia.'),
(162, 'Ciliata', 'Suatu klas protozoa (subfilum Ciliophora) yang anggotanya memiliki silia selama siklus hidupnya, beberapa di antaranya bersifat parasit.'),
(163, 'Cimex', 'Suatu genus insekta famili Cimicidae.'),
(164, 'Cittotaenia', 'Suatu genus cacing pita famili Anoplocephalidae.'),
(165, 'Cladophora', 'Suatu genus ganggang yang ditemukan dalam jumlah banyak pada insang ikan yang mati karena asfiksia.'),
(166, 'Cladosporium', 'Suatu genus fungus.'),
(167, 'Cladotaenia', 'Suatu genus cacing pita yang ditemukan pada burung liar.'),
(168, 'Clavicipitaceae', 'Famili fungus yang mencakup juga Claviceps spp. Anggotanya mempunyai askus tubuler yang panjang dan askospora lirbenang.'),
(169, 'Cleiodiscus', 'Suatu genus cacing daun famili Dactylogyridae yang menginfestasi insang ikan.'),
(170, 'Cnemidocoptes ', 'Suatu genus tungau famili Sarcoptidae.'),
(171, 'Cnephia ', 'Lalat hitam kecil yang menyebabkan gangguan yang hebat pada ternak. Disebut juga lalat hitam.'),
(172, 'Coccidioides ', 'Suatu genus fungus patogenik.'),
(173, 'Cochliomyia ', 'Suatu genus lalat dari famili insekta Calliphoridae.'),
(174, 'Cochlosoma ', 'Parasit protozoa famili Cochlosomatidae.'),
(175, 'Coloceras ', 'Kutu.'),
(176, 'Colpocephalum ', 'Suatu genus kutu.'),
(177, 'Conidiobolus ', 'Suatu genus fungus yang berada di tanah.'),
(178, 'Corynebacteriaceae ', 'Suatu famili bakteria, biasanya terdiri atas batang-batang nonmotil terkadang menunjukkan variasi nyata dalam bentuknya dan terkadang berbintik atau bergaris dengan granula metakromatik. Mengandung genus Corynebacterium.'),
(179, 'Corynosoma ', 'Suatu genus parasit akantosefala famili Polymorphidae. Disebut juga cacing kepala berduri.'),
(180, 'Costotomi ', 'Pengirisan atau pemecahan tulang rawan pada tulang rusuk.'),
(181, 'Costovertebral ', 'Ada hubungannya dengan tulang rusuk dan tulang belakang.'),
(182, 'Cottoidei ', 'Subordo ikan dari ordo Perciformes, penting karena terdiri atas sejumlah besar spesies beracun.'),
(183, 'Cotylophoron ', 'Cacing daun dalam lambung.'),
(184, 'Crepidostomum ', 'Parasit tremetoda pada salmonid dan ikan lain. Genus ini anggota famili Allocreadiidae.'),
(185, 'Dacryoadenektomi', 'Pemotongan kelenjar airmata.'),
(186, 'Dacryoadenitis ', 'Radang kelenjar airmata.'),
(187, 'Dacryoblenorea ', 'Aliran mukus dari aparat airmata.'),
(188, 'Dakriagogik ', 'Merangsang keluarnya airmata atau berfungsi sebagai saluran untuk luberan sekresi kelenjar air mata.'),
(189, 'Dakrioadenalgia ', 'Nyeri kelenjar airmata.'),
(190, 'Dakriohemorea ', 'Leleran air mata bercampur darah.'),
(191, 'Dakrioma ', 'Suatu pembengkakan lirtumor karena penyumbatan saluran air mata.'),
(192, 'Dakrion ', 'Titik pertemuan antar tulang rahang atas, tulang dahi dan tulang air mata.'),
(193, 'Dakriopiorea ', 'Leleran air mata bercampur nanah.'),
(194, 'Dakriorea ', 'Aliran air mata yang berlebihan.'),
(195, 'Dakriosistalgia ', 'Nyeri kantong air mata. Juga disebut, dakrioadenalgia.'),
(196, 'Dakriosistektomi ', 'Pemotongan dinding kantong air mata.'),
(197, 'Dakriosistitis ', 'Radang kantong air mata.'),
(198, 'Dakriosistoblenorea ', 'Radang kataral kronik kantong air mata, dengan penyempitan kelenjar air mata.'),
(199, 'Dakriosistorinostenosis ', 'Penyempitan saluran antara kantong air mata dan rongga hidung.'),
(200, 'Dakriosistorinostomi ', 'Pembentukan lubang dengan pembedahan antara kantong air mata dengan rongga hidung.'),
(201, 'Dakriosistostenosis ', 'Penyempitan kantong air mata.'),
(202, 'Dakriosistostomi ', 'Pembuatan lubang baru pada kantong air mata secara bedah disertai drainase.'),
(203, 'Dakriosistotomi ', 'Penorehan kantong air mata dan salurannya.'),
(204, 'Dakriosit ', 'Suatu bentuk sel darah merah berbentuk tetes air mata, ditemukan dalam anemia fragmentasi dan mielofibrosis.'),
(205, 'Dakriosolenitis ', 'Radang saluran air mata.'),
(206, 'Dakriostenosis ', 'Penyempitan saluran air mata.'),
(207, 'Daktil ', 'Suatu jari.'),
(208, 'Daktilitis ', 'Radang jari.'),
(209, 'Daktilolisis ', 'Koreksi sindatili dengan pembedahan, penghilangan atau amputasi jari.'),
(210, 'Daktilomegali', 'Jari besar yang tak normal.'),
(211, 'Dantrolen ', 'Relaksan otot skelet, pengaruh pertama pada hubungan otot syaraf, jaringan otot, pengaruh kedua pada sistem syaraf, jaringan otot pusat.'),
(212, 'Darurat ', 'Suatu kejadian tiba-tiba dan tidak diharapkan yang membutuhkan perhatian segera. Kebanyakan praktek dokter hewan didasarkan pada kebutuhan untuk penanganan darurat hewan, pada pengobatan, pembedahan dan reproduksi. Kedokteran preventif adalah suatu bagian praktek semacam itu selama beberapa tahun dan sedang berkembang macam-macam pelayanan yang memasukkan hal-hal seperti, program kesehatan kelompok, pengawasan sitogenetik, transfer embrio.'),
(213, 'Debilitasi ', 'Dalam keadaan lemah.'),
(214, 'Debris ', 'Jaringan mati atau bahan asing.'),
(215, 'Debridemen ', 'Pembuangan semua materi asing, jaringan terkontaminasi dan jaringan mati dari atau di dekat lesi traumatik atau terinfeksi sampai jaringan sehat di sekelilingnya terpapar.'),
(216, 'Debulking ', 'Pembersihan kelebihan jaringan dari les untuk membantu proses penyembuhan atau sebagai pendukung kemoterapi.'),
(217, 'Defek ', 'Suatu ketaksempurnaan, kegagalan atau ketiadaan.'),
(218, 'Defekasi ', 'Pembuangan limbah dan makanan tak tercerna melalui rektum sebagai tinja.'),
(219, 'Deferentitis ', 'Radang duktus deferens.'),
(220, 'Defibrilasi ', 'Penghentian fibrilasi ventrikel dan atrium, biasanya dengan gugat listrik atau pemisahan serabut-serabut jaringan menggunakan pisau tumpul.'),
(221, 'Defibrilator ', 'Suatu perangkat yang digunakan untuk menimbulkan defibrilasi dengan memberikan kejutan listrik singkat pada jantung, secara langsung atau melalui elektroda yang ditempatkan pada dinding dada.'),
(222, 'Defibrinasi ', 'Penghancuran atau pengambilan fibrin, misalnya dari darah.'),
(223, 'Defeminisasi ', 'Kehilangan ciri-ciri kelamin betina.'),
(224, 'Defisiensi ', 'Ketiadaan atau kekurangan, suatu kondisi yang menciri dengan terdapatnya suplei atau kompetensi yang kurang dari normal atau kekurangan dari yang diperlukan.'),
(225, 'Defluorinasi ', 'Turunnya kandungan fluor dalam makanan sampai di bawah kadar yang diakui sebagai nontoksik.'),
(226, 'Defluvium ', 'Rontok, misalnya bulu.'),
(227, 'Deformasi ', 'Deformitas, terutama perubahan bentuk, struktur atau proses adaptasi bentuk dan wujud.'),
(228, 'Degenerasi ', 'Kemunduran, perubahan suatu bentuk lebih tinggi ke lebih rendah terutama perubahan jaringan menjadi bentuk lebih rendah atau yang secara fungsional kurang aktif. Degenerasi sejati bila ada perubahan kimiawi jaringan, infiltrasi bila perubahan berupa deposit materi abnormal dalam jaringan.'),
(229, 'Deglutisi ', 'Aksi penelanan. Ada perbedaan mekansisme antara burung dan mamalia kebanyakan burung tak dapat menelan tanpa mengangkat kepalanya, kemungkinan karena tak punya palatum lunak untuk menutup rongga hidung. Tembolok bertindak sebagai penyangga sehingga jika lambung penuh, burung masih dapat terus makan, tetapi makanan disimpangkan ke tembolok.'),
(230, 'Dehidrasi ', 'Kondisi tubuh lebih banyak kehilangan air dari pada yang diperelah. Ada suatu keseimbangan zalir negatif, sehingga volume darah sirkulasi menurun dan zalir jaringan berkurang dan terjadi dehidrasi jaringan. Sindrom klinis dehidrasi termasuk kulit rongga dan keriput, suatu reaksi skin-tenting jelek yaitu hilangnya bekas cubitan kulit lebih lama dari normal, biasanya ada petunjuk penyebab dehidrasi, misalnya muntah, poliuri. Uji patologi klinis penting untuk menentukan kehebatan dehidrasi.'),
(231, 'Dehidroemetin ', 'Suatu obat anti ameba.'),
(232, 'Dehidroepiandrosteron ', 'Suatu androgen yang terdapat dalam urin normal manusia dan disintesa dari kolesterol.'),
(233, 'Dehidrokolesterol ', 'Suatu sterol terdapat dalam kulit yang bila diradiasi secara wajar dengan sinar ultraviolet membentuk vitamin D.'),
(234, 'Dekalsifikasi ', 'Proses pembuangan bahan berkapur dan hilangnya garam-garam kalsium dari tulang atau gigi.'),
(235, 'Dekantasi ', 'Pembuangan cairan supernatant dari sedimen.'),
(236, 'Dekapitasi ', 'Pemisahan kepala.'),
(237, 'Dekapsulasi ', 'Penghilangan kapsula, terutama kapsula renalis.'),
(238, 'Dekolesterolisasi ', 'Pengurangan kadar kolesterol dalam darah.'),
(239, 'Dekolorasi ', 'Pembuangan warna dan tak punya atau hilangnya warna.'),
(240, 'Dekompensasi ', 'Kegagalan kompensasi.'),
(241, 'Dekontaminasi ', 'Pembebasan pasien atau obyek dari zat pencemar, seperti gas perang, bahan radioaktif dll.'),
(242, 'Dekoquinat ', 'Suatu quinolone antikoksidia yang nontoksik namun dapat dengan cepat mengembangkan koksidia resisten.'),
(243, 'Dekrudesens ', 'Menjadi berkurangnya atau kurang hebatnya intensitas tanda-tanda klinis.'),
(244, 'Dekstranomer ', 'Manik-manik kecil polimer dekstran yang sangat hidrofil digunakan untuk pembersih debris luka, seperti tukak stasis vena manik-manik steril dituang pada luka untuk menyerap eksudat dan mencegah pembentukan keropeng.'),
(245, 'Dekstrinosis ', 'Suatu kondisi berciri adanya akumulasi suatu polisakarida abnormal dalam jaringan.'),
(246, 'Dekstrinuria ', 'Adanya dekstrin dalam urine.'),
(247, 'Elaborasi ', 'Proses untuk menghasilkan substansi kompleks dari materi yang lebih sederhana.'),
(248, 'Eburnasi ', 'Konversi tulang menjadi suatu massa keras lirgading.'),
(249, 'Echinostomatidae ', 'Suatu famili cacing daun, dijumpai pada burung.'),
(250, 'Edensia ', 'Tiadanya gigi.'),
(251, 'Edentat ', 'Suatu hewan tanpa gigi, misalnya, tenggiling raksasa atau pemakaian dalam arti yang benar, suatu anggota hewan ordo Edentata, termasuk tenggiling dan kungkang.'),
(252, 'Edentulus ', 'Tanpa gigi.'),
(253, 'Edwardsiella ', 'Suatu genus bakteri suku Escherichiae. Ditemukan dalam usus ular, ditemukan juga dalam air.'),
(254, 'Efektor ', 'Suatu otot atau kelenjar, berturut-turut yang berkontraksi dan bersekresi dalam responnya terhadap impuls syaraf atau suatu molekul yang mengikat suatu enzim dengan efek pada aktivitas katalitiknya, baik yang bersifat activator maupun yang bersifat inhibitor.'),
(255, 'Ejakulasi ', 'Ekspulsi dengan tiba-tiba dengan kekuatan, terutama ekspulsi semen dari uretra jantan, suatu refleks yang terjadi sebagai suatu akibat rangsangan seksual. Termasuk zalir lubrikasi dari kelenjar bulbo-uretralis, zalir kantong postrata dan semen cairan.'),
(256, 'Ekaudat ', 'Tidak berekor.'),
(257, 'Ekdisis ', 'Pengelupasan lapisan luar kulit hanya epidermis yang berpartisipasi. Dikontrol kelenjar endokrin. Dapat bersifat komplit atau inkomplit, lazimnya karena nutrisi jelek. Disebut juga eksuviat.'),
(258, 'Ekimoma ', 'Pembengkakan karena ekstravasasi darah.'),
(259, 'Ekinokokosis', 'Suatu infeksi manusia dan hewan, lazimnya hati dan paru disebabkan stadium larva (sista hidatida) cacing pita genus Echinococcus ditandai dengan perkembangan sista yang meluas.'),
(260, 'Eklamptogenik ', 'Menyebabkan eklamsia.'),
(261, 'Ekondroma ', 'Suatu pertumbuhan hiperplasi jaringan kartilago pada permukaan suatu kartilago atau memproyeksi di bawah periosteum suatu tulang.'),
(262, 'Eksanguinasi ', 'Kehilangan darah yang ekstensif karena pendarahan internal dan eksternal.'),
(263, 'Eksantem ', 'Setiap penyakit erupsi kulit atau demam.'),
(264, 'Eksantematus ', 'Tersirat dengan atau dalam keadaan erupsi atau ruam.'),
(265, 'Eksartikulasi ', 'Pemotongan pada suatu sendi, pengambilan sebagian suatu sendi.'),
(266, 'Eksaserbasi ', 'Peningkatan dalam kehebatan suatu penyakit atau setiap tanda klinis.'),
(267, 'Eksensefali ', 'Suatu cacat kongenital, otak secara sempurna terlihat atau menonjol melalui suatu cacat pada kolong tengkorak.'),
(268, 'Eksenterasi ', 'Pembuangan dengan pembedahan semua organ internal, misalnya yang dari mata, atau ruang pelvis.'),
(269, 'Ekserebrasi ', 'Pengamilan otak.'),
(270, 'Ekseresis ', 'Pengambilan dengan pembedahan atau eksisi.'),
(271, 'Eksflagelasi ', 'Penonjolan atau pembentukan mikrogamet berflagela dari suatu mikrogametosit pada beberapa sporozoa.'),
(272, 'Eksikasi ', 'Tindakan pengeringan, dalam kimia, penghilangan suatu substansi kristal dari air kristalisasinya.'),
(273, 'Eksim ', 'Suatu istilah umum untuk setiap keradangan yang melibat mulanya epidermis, awalnya bertanda kemerah-merahan, kegatalan, berair, mengucur, berkeropeng dan kemudian bersisik, berlapis lir-lumut dan sering pigmentasi atau dermatitis atopik.'),
(274, 'Eksipien ', 'Setiap substansi yang agak lembam ditambahkan dalam suatu obat untuk memberikan konsistensi atau bentuk yang cocok pada obat, suatu pembawa.'),
(275, 'Eksisi ', 'Mengambil dengan memotong.'),
(276, 'Eksitan ', 'Suatu agen penyebab eksitasi fungsi vital atau eksitasi fungsi otak.'),
(277, 'Eksitasi ', 'Suatu tindakan mengiritasi atau menstimulasi, suatu kondisi sedang tereksitasi atau sedang merespon stimuli, penambahan energi, seperti eksitasi suatu molekul dengan absorbsi foton.'),
(278, 'Eksitosekretori ', 'Menghasilkan sekresi meningkat.'),
(279, 'Eksitovaskuler ', 'Menyebabkan perubahan pembuluh darah.'),
(280, 'Eksiviat ', 'Menyilih atau kulit terkelupas.'),
(281, 'Eksklaf ', 'Bagian suatu organ yang terlepas.'),
(282, 'Eksklusi ', 'Suatu penghentian atau eliminasi, isolasi suatu bagian dengan pembedahan, seperti isolasi segmen intestinum tanpa pengambilannya dari tubuh.'),
(283, 'Ekskremen ', 'Materi feses, materi yang lepas keluar sebagai sampah tubuh.'),
(284, 'Eksodeviasi', 'Suatu pembelokan ke arah luar, pada oftalmologi eksotropia.'),
(285, 'Eksoenzim ', 'Suatu enzim yang bekerja di luar sel yang mengeksresikan.'),
(286, 'Eksoeritrositik ', 'Terdapat atau berada di luar sel darah merah (eritrosit), suatu istilah yang digunakan pada suatu stadium dalam perkembangan parasit protozoa yang terjadi pada sel-sel lain selain eritrosit.'),
(287, 'Eksogenus ', 'Berasal dari luar atau disebabkan oleh faktor di luar organisme.'),
(288, 'Eksokardia ', 'Salah letak jantung bersifat kongenital, ektokardia.'),
(289, 'Eksokardial ', 'Berada, terdapat atau berkembang diluar jantung.'),
(290, 'Eksokolitis ', 'Peradangan lapisan luar kolon.'),
(291, 'Eksorbitisme ', 'Penonjolan bola mata.'),
(292, 'Eksoriasi ', 'Lecet trauma permukaan dan garutan yang mengambil substansi kulit. Lumrahnya disebabkan pada hewan dengan menggosok atau menggaruk kulit yang gatal.'),
(293, 'Eksormia ', 'Suatu erupsi kulit berbentuk papula.'),
(294, 'Eksositosis ', 'Pelepasan dari suatu sel partikel yang terlalu besar untuk difusi melalui dinding, lawannya endositosis atau agregasi lekosit yang migrasi pada epidermis sebagai bagian respon radang.'),
(295, 'Eksoskeleton', 'Suatu kerangka keras bagian luar, seperti kulit udang, yang mendukung dan melindungi jaringan lunak hewan tingkat rendah, berasal dari ektodermis. Pada vertebrata, istilah tersebut terkadang dipakai untuk struktur yang dihasilkan oleh epidermis, seperti rambut, cakar, teracak, gigi dsb.'),
(296, 'Eksosmosis ', 'Osmosis atau difusi dari dalam ke luar.'),
(297, 'Eksotermal ', 'Ditandai atau diikuti dengan timbulnya panas, membebaskan panas atau energi.'),
(298, 'Eksotoksin ', 'Suatu toksin yang kuat dibentuk dan diekskresikan oleh sel bakteri dan ditemukan bebas dalam medium sekitarnya. Adj. esksotoksik. Eksotoksin merupakan protein dan sifatnya labil terhadap panas. Eksotoksin dinetralkan dengan hambatan antigenesitasnya dengan menggunakan formaldehid (toksoid) dan substansi yang paling beracun dikenal pada manusia. Bakteri genus Clostridium adalah penghasil eksotoksin paling sering, edema usus babi, botulisme dan tetanus semuanya disebabkan oleh toksin bakteri.'),
(299, 'Ekspektoran ', 'Merangsang ekspektorasi atau suatu agen yang merangsang ekspektorasi.'),
(300, 'Ekspektorasi ', 'Pembatukan dan sekaligus penelanan materi dari paru, bronkus dan trakea. Pada manusia materi yang dibatukan dibuang dengan meludah atau sputum.'),
(301, 'Ekspirat ', 'Udara atau gas yang dihembuskan.'),
(302, 'Eksplan ', 'Mengambil dari tubuh dan tempat dalam media buatan untuk pertumbuhan atau jaringan yang diambil dari dan ditumbuhkan dalam medium buatan.'),
(303, 'Ekspulsif ', 'Menggerakkan atau memaksa keluar, cenderung menolak.'),
(304, 'Ekstensi ', 'Gerakan yang menyebabkan dua ujung setiap bagian yang bergabung tertarik jauh satu sama lain atau gerakan yang membawa anggota suatu alat gerak menjadi atau menuju suatu kondisi lurus.'),
(305, 'Eksterminasi ', 'Pembunuhan massal hewan atau ternak lainnya. Berimplikasi penghancuran total spesies atau kelompok lainnya.'),
(306, 'Ekstern ', 'Suatu pelajar kedokteran hewan atau lulusan pada ilmu kedokteran hewan yang membantu dalam perawatan pasien di rumah sakit, tetapi tidak tinggal di sana.'),
(307, 'Eksteroseptor ', 'Suatu ujung saraf sensoris yang distimulasi oleh lingkungan luar terdekat, seperti ujung saraf sensoris pada kulit dan membrane mukosa. Ajektif eksteroseptif.'),
(308, 'Ekstirpasi ', 'Pengambilan atau eradikasi total suatu organ atau jaringan.'),
(309, 'Fading ', 'Penurunan dalam kondisi tubuh, kesehatan umum, aktivitas dan produktivitas.'),
(310, 'Faint ', 'Kehilangan temporer kesadaran karena iskemi serebral umum, sinkop. Istilah tersebut secara umum tidak digunakan pada hewan.'),
(311, 'Faking ', 'Perubahan tidak wajar penampilan seekor kuda untuk tujuan kecurangan. Biasanya dihubungkan dengan gigi.'),
(312, 'Famili ', 'Suatu kelompok hewan yang berhubungan dengan golongan darah dan turunan  atau suatu kategori taksonomi di bawah suatu ordo dan di atas suatu genus.'),
(313, 'Faset ', 'Suatu permukaan datar dan kecil pada suatu bagian tubuh yang keras seperti tulang.'),
(314, 'Fasilitatif ', 'Dalam farmakologi, menunjukkan suatu reaksi yang muncul, sebagai akibat tidak langsung kerja obat, seperti misalnya perkembangan suatu infeksi setelah mikroflora normal terubah oleh suatu antibiotik.'),
(315, 'Fasiobrakial ', 'Berkaitan dengan wajah dan anggota gerak depan atas.'),
(316, 'Fasiodesis ', 'Jahitan suatu fasia pada perlekatan kerangka.'),
(317, 'Fasiola ', 'Jamak fasiolae. Suatu struktur kecil lirkain dan pita atau suatu pembalut kecil.'),
(318, 'Fasiolingual ', 'Berkaitan dengan wajah dan lidah.'),
(319, 'Fasioplasti ', 'Perbaikan plastik suatu fasia.'),
(320, 'Fasiorafi ', 'Kesembuhan suatu fasia yang robek.'),
(321, 'Fasioservikal ', 'Berkaitan dengan wajah dan leher.'),
(322, 'Fatal ', 'Menyebabkan kematian, mati, mortal, letal.'),
(323, 'Favus ', 'Suatu penyakit unggas disebabkan oleh Trichophyton gallinae. Bercak putih kecil tampak pada jengger, kemudian bergabung dan menebal. Bila lesi menyebar ke bagian berbulu akan terbentuk keropeng lirtameng favus yang khas. Pada kasus yang berlangsung lama, keropeng pada kulit leher dapat mampat berdekatan, dan dengan pusatnya yang terdepres, memberikan suatu bentuk sarang lebah, sehingga dinamai cacing gelang sarang lebah.'),
(324, 'Fekal ', 'Berkaitan dengan feses atau berkaitan sifat feses.'),
(325, 'Fekaloid ', 'Menyerupai feses.'),
(326, 'Fenfluramin ', 'Suatu anorektik yang tampaknya lebih bersifat mendepres daripada merangsang sistem syaraf pusat seperti yang dilakukan amfetamin, yang digunakan sebagai garam hidroklorid.'),
(327, 'Fenoprofen ', 'Suatu obat anti radang nirsteroid.'),
(328, 'Feredoksin ', 'Suatu protein yang mengandung besi bukan hem yang punya potensi redoks sangat rendah, feredoksin berperan dalam transpor elektron pada fotosintesis, fiksasi nitrogen dan berbagai proses biologi lain.'),
(329, 'Fertilisasi ', 'Pada reproduksi hewan, proses penggabungan sperma dan ovum. Kejadian ini disebut juga konsepsi, penggabungan gamet jantan dan betina membentuk suatu zigot sel tunggal. Bila satu spermatozoa menembus ovum, terjadi suatu reaksi yang mencegah masuknya setiap spermatozoon lain lebih lanjut. Inti spermatozoa dan ovum bergabung dalam suatu proses yang disebut singami dan fertilisasi menjadi sempurna. Spermatozoon tinggal dalam saluran reproduksi betina dan menjaga fertilitasinya selama 5 hari pada kuda betina, 90 jam pada anjing betina, dan selama 24-48 jam pada sapi betina, domba betina dan babi betina. Pada individu betina, ovarium yang berevolusi tetap dapat hidup dalam saluran selama 12 – 18 jam.'),
(330, 'Fervesen ', 'Peningkatan demam atau peningkatan suhu tubuh.'),
(331, 'Feses ', 'Sampah tubuh yang dibuang dari usus, disebut juga tinja, ekskreta atau ekskremen. Feses terbentuk dalam kolon dan menurun ke rektum dengan proses peristaltik. Bila rektum penuh, ujung syaraf pada dinding rektum memberi isarat suatu kebutuhan pengosongan yang mungkin terbentuk dengan suatu relaksasi sadar otot sfingter di sekitar bagian luar anus.'),
(332, 'Fetal ', 'Dari dan berkaitan dengan suatu fetus atau dengan suatu periode perkembangannya.'),
(333, 'Fetotomi ', 'Pemotongan suatu fetus yang mati dalam uterus. Dapat dipraktekkan terutama pada sapi karena ukuran uterus dan kesempatan memasukkan perkakas ke dalam kejelukan fetus.'),
(334, 'Fiberskop ', 'Suatu endoskop fleksibel yang lumennya terbungkus dengan serabut optik gelas atau serabut plastik yang punya sifat optik khusus.'),
(335, 'Fibrilogenesis ', 'Pembentukkan dan perkembangan fibril.'),
(336, 'Fibroma ', 'Suatu tumor terutama tersusun dari fibrosa atau jaringan ikat yang terkembang secara penuh. Lumrahnya berhubungan dengan tempat-tempat khusus misalnya, hidung, genital, larinks, tangkai serebrum yang menimbulkan beberapa sindrom klinis.'),
(337, 'Fibromiitis ', 'Keradangan otot dengan degenerasi fibrus.'),
(338, 'Fiksatif ', 'Suatu agen seperti formalin yang digunakan untuk pengawet spesimen histologi atau patologis untuk mempertahankan struktur normal elemen-elemen penyusunnya.'),
(339, 'Folikulitis ', 'Radang folikel, galibnya digunakan berkenaan dengan folikel rambut, tetapi kadang-kadang juga folikel lainnya.'),
(340, 'Folikuler ', 'Berhubungan dengan atau mengenai folikel.'),
(341, 'Footrot ', 'Suatu penyakit kuku menciri dengan dermatitis pada kulit antar jari dengan beberapa perluasan pada jaringan tanduk khususnya pada tumit. Lumrahnya infeksi di bawah jaringan tanduk. Kebanyakan bentuk penyakit adalah infeksius dan disebabkan oleh bakteri. Disebut juga pododermatitis.'),
(342, 'Formaldehida ', 'Senyawa berbentuk gas bersifat desinfektan kuat. Digunakan dalam larutan (formol) untuk desinfektan ekskret dari gawai juga dalam pembuatan toksoid dari toksin dan sebagai gas untuk pengasapan pada penetasan telur.'),
(343, 'Formamidin', 'Suatu kelompok senyawa akarisida, digunakan sebagai semprot tumbuhan dan secara oles pada hewan, terutama dalam pengobatan kudis demodek.'),
(344, 'Formisiasi ', 'Suatu kondisi morbid disebabkan oleh gigitan semut.'),
(345, 'Foses ', 'Lintasan dari mulut sampai farinks. Adj, fosial.'),
(346, 'Fositis ', 'Keradangan foses.'),
(347, 'Fraktur ', 'Retaknya suatu bagian, terutama tulang atau putusnya kontinuitas tulang. Fraktur dapat disebabkan oleh trauma, oleh puntiran lantaran kejang otot, dan hilangnya kekuatan mengungkit secara tak langsung, atau oleh penyakit yang mengakibatkan dekalsifikasi tulang dan juga yang berhubungan dengan tulang.'),
(348, 'Fremitus ', 'Getaran yang terasa pada palpasi atau auskultasi, misalnya teraba pada arteri uterina mediana pada sapi bunting tua, teraba pada kelenjar tiroid yang membesar karena gondok.'),
(349, 'Frenulum ', 'Jamak, frenula. Suatu lipatan kecil dari integumen atau membran mukosa yang membatasi gerakan suatu organ atau bagian.'),
(350, 'Fruktosuria ', 'Adanya fruktosa dalam kemih.'),
(351, 'Fulminan ', 'Penyakit yang terjadi secara sangat mendadak dan hebat.'),
(352, 'Fumagilin ', 'Suatu antibiotik yang dihasilkan oleh Aspergillus fumigatus digunakan untuk pengobatan ensefalitozoonosis.'),
(353, 'Fumarin ', 'Sama dengan kumarin digunakan sebagai rodentisida disebut juga kumafuril.'),
(354, 'Fumitoksin ', 'Zat toksik yang ditemukan pada Aspergillus fumigatus.'),
(355, 'Fumitori ', 'Suatu tumbuhan yang dicurigai beracun.'),
(356, 'Fumitremorgen ', 'Substansi toksik yang diisolasi dari Aspergillus fumigatus. Menyebabkan gangguan koordinasi (ataksia), kesadaran menurun (letargi), hipersensitif dan seperti gila pada kuda.'),
(357, 'Fundektomi ', 'Eksisi pada fundus suatu organ, seperti pada lambung.'),
(358, 'Fundusektomi ', 'Eksisi fundus lambung.'),
(359, 'Fungemia ', 'Terdapatnya fungus di dalam darah yang beredar.'),
(360, 'Fungisidia ', 'Agen yang merusak fungi. Adj, fungisidial.'),
(361, 'Fungitoksik ', 'Menggunakan pengaruh toksik terhadap fungus.'),
(362, 'Fungosa ', 'Berujud, disebabkan oleh atau menyerupai fungus.'),
(363, 'Funikulitis ', 'Radang pada tali mania tau radang pada serabut syaraf spinal yang terletak dalam kanalis vertebralis.'),
(364, 'Funikuloepididimitis ', 'Radang tali mani dan epididymis (anak buah zakar).'),
(365, 'Furmosemida ', 'Frusemid, suatu diuretik.'),
(366, 'Furunkel ', 'Radang bernanah dan terbatas pada kulit dan jaringan bawah kulit, membungkus massa jaringan mati sentral, disebut juga bisul.'),
(367, 'Furunkoloid ', 'Menyerupai suatu furunkel atau bisul.'),
(368, 'Fusibel', 'Mampu mencairkan.'),
(369, 'Fusiform ', 'Bentuk kumparan.'),
(370, 'Fusin ', 'Pigmen coklat pada epitel retina.'),
(371, 'Galaktemia ', 'Terdapatnya air susu dalam darah.'),
(372, 'Galaktiskia ', 'Penekanan sekresi air susu.'),
(373, 'Galaktofora ', 'Saluran (duktus) air susu.'),
(374, 'Galaktopoiesis ', 'Pembentukan air susu oleh kelenjar susu.'),
(375, 'Galaktorea ', 'Mengalirnya air susu secara spontan dan deras, air susu tetap mengalir pada saat setelah disuse atau disapih.'),
(376, 'Galaktosemia ', 'Kekacauan biokomia karena defisiensi enzim yang diperlukan untuk metabolisme galaktosa. Kondisi ini diturunkan pada manusia dalam dua bentuk, dapat karena defisiensi galaktokinase atau galaktosa-1-fosfat uridiltransferase. Makropoda dewasa biasanya defisiensi terhadap enzim-enzim ini. Biasanya gula berasal dari laktosa air susu diubah melalui aksi enzimatik menjadi glukosa. Bila konversi dari galaktosa menjadi glukosa gagal, galaktosa tertimbun dalam jaringan dan darah, secara khas membentuk katarak, secara umum terlihat pada makropoda muda yang dibesarkan dengan susu sapi.'),
(377, 'Galaktostasis ', 'Berhentinya sekresi air susu, bendung air susu dalam kelenjar susu.'),
(378, 'Ganglionitis ', 'Radang pada ganglion, gangliitis.'),
(379, 'Gangliosidosis ', 'Suatu kelompok gangguan penyimpanan lemak secara herediter ditandai dengan akumulasi ganglioside dalam jaringan karena cacat enzim. Menciri dengan disfungsi neuromuskuler secara progresif dan gangguan pertumbuhan sejak umur awal.'),
(380, 'Gasterofilosis ', 'Suatu penyakit disebabkan oleh infestasi Gasterophilus spp. Penyakit sporadik dengan pembentukan abses, bahkan dinding lambung robek (ruptur) dengan peritonitis lokal, meskipun infestasi terjadi secara dramatik, sebetulnya nonpatogenik.'),
(381, 'Gastradenitis ', 'Radang kelenjar-kelenjar lambung.'),
(382, 'Gastrodinia ', 'Nyeri lambung.'),
(383, 'Gastroduodenitis ', 'Radang lambung dan duodenum.'),
(384, 'Gastroduodenoskopi ', 'Pemeriksaan endoskopik pada lambung dan duodenum.'),
(385, 'Gastroenteralgia ', 'Nyeri lambung dan usus.'),
(386, 'Gastroenterokolitis ', 'Radang lambung, usus kecil dan kolon.'),
(387, 'Gastroenteropati ', 'Setiap penyakit lambung dan usus.'),
(388, 'Gastroenteroptosis ', 'Perpindahan letak ke bawah atau prolaps lambung dan usus.'),
(389, 'Gastrogenik ', 'Berasal dari lambung.'),
(390, 'Gastrohepatitis ', 'Radang lambung dan hati.'),
(391, 'Gastrojejunal ', 'Mengenai lambung dan jejunum.'),
(392, 'Gastrokolitis ', 'Radang lambung dan kolon.'),
(393, 'Gastrolava ', 'Irigasi lambung melalui hiliran lambung.'),
(394, 'Gastroparalisis ', 'Kelumpuhan lambung, gastroplegia.'),
(395, 'Gastropati ', 'Setiap penyakit pada lambung.'),
(396, 'Gastropilorik ', 'Berkaitan dengan lambung dan pilorus.'),
(397, 'Gastroplikasi ', 'Pengobatan dilatasi lambung dengan menjahit suatu lipatan pada dinding lambung.'),
(398, 'Gastropulmoner ', 'Berkaitan dengan lambung dan paru.'),
(399, 'Gastrorafi ', 'Jahitan pada lambung.'),
(400, 'Gastroragia ', 'Perdarahan pada lambung.'),
(401, 'Gastrorea ', 'Sekresi berlebihan oleh kelenjar-kelenjar lambung.'),
(402, 'Gastrospasmus ', 'Kejang lambung.'),
(403, 'Gastrostenosis ', 'Kontraksi atau pengerutan lambung.'),
(404, 'Gastrostaksis ', 'Keluarnya darah dari mukosa lambung.'),
(405, 'Gastroptosis ', 'Perpindahan letak lambung ke bawah.'),
(406, 'Gelosis ', 'Keras, pembengkakan jaringan khususnya pada otot.'),
(407, 'Genik ', 'Mengenai atau disebabkan oleh gen.'),
(408, 'Genikulat ', 'Bengkok, seperti lutut.'),
(409, 'Genital ', 'Berkaitan dengan reproduksi, atau organ reproduksi.'),
(410, 'Genitoplasti ', 'Bedah plastik pada organ reproduksi (kelamin).'),
(411, 'Genodermatosis ', 'Suatu kekacauan genetik pada kulit, lazimnya bersifat umum.'),
(412, 'Geotrikosis ', 'Suatu infeksi lirkandidiasis, disebabkan oleh Geotrichum candidum, dapat menyerang bronkus, paru, mulut atau saluran usus.'),
(413, 'Geratik ', 'Berhubungan dengan umur tua.'),
(414, 'Germisida ', 'Agen yang dapat merusak mikroorganisme patogen.'),
(415, 'Geroderma ', 'Gerodermia, distrofi kulit dan kelamin, tampak pada umur tua.'),
(416, 'Geromarasmus ', 'Kekurusan kadang-kadang tersifat pada umur tua.'),
(417, 'Gestosis ', 'Setiap manifestasi keracunan kehamilan.'),
(418, 'Gibositas ', 'Keadaan bungkuk, kifosis.'),
(419, 'Ginandroblastoma ', 'Suatu tumor ovarium yang mengandung elemen arenoblastoma dan tumor sel granulosa, ini menghasilkan efek androgenik dan estrogenik.'),
(420, 'Ginandromorfisme ', 'Terdapatnya kromosom dari kelamin dalam jaringan tubuh yang berbeda, yang menghasilkan mosaik kelamin jantan dan betina yang menciri.'),
(421, 'Gingivalgia ', 'Nyeri pada gusi.'),
(422, 'Gingivolabial ', 'Mengenai gusi dan bibir.'),
(423, 'Gingivostomatitis ', 'Radang pada gusi dan mukosa mulut.'),
(424, 'Girospasmus ', 'Kejang berputar pada kepala.'),
(425, 'Glandula ', 'Kelenjar kecil.'),
(426, 'Glaukoma ', 'Penyakit pada mata yang tersifat dengan meningkatnya tekanan intraokuler, mengakibatkan perubahan-perubahan patologik pada diskus optikus dan khusus menyebabkan kerusakan bidang visual, akhirnya but ajika tidak mendapatkan pengobatan yang baik.'),
(427, 'Glikemia ', 'Terdapatnya glukosa dalam darah.'),
(428, 'Glikosialorea ', 'Aliran ludah yang berlebihan yang mengandung gula.'),
(429, 'Glomerulitis ', 'Radang glomerulus ginjal.'),
(430, 'Glukokortikoid ', 'Setiap substansi kortikoid yang meningkatkan gluconeogenesis, meningkatkan konsentrasi glikogen hati dan gula darah, yakni kortisol (hidrokortison), kortison dan kotikosteron. Substansi-substansi ini secara luas digunakan sebagai agen anti radang, juga efektif untuk menghentikan kebuntingan tahap akhir dan digunakan sebagai alat manajemen pada sapi untuk maksud tersebut.'),
(431, 'Glukoneogenesis ', 'Sintetis glukosa dari sumber nirkarbohidrat, seperti asam amino, propionat dan gliserol. Terjadi terutama pada hati dan ginjal jika suplai karbohidrat tak cukup untuk memenuhi kebutuhan metabolisme tubuh. Glukoneogenesis distimulasi oleh kortisol dan GLUKOKORTIKOID lain serta glukagon.'),
(432, 'Glukosuria ', 'Terdapatnya glukosa dalam urine atau dekstrosuria. Disebut juga glikosuria.'),
(433, 'Hemoglobinuria ', 'Terdapatnya hemoglobin bebas dalam urine. Pada hemoglobinuria sejati proteinnya berasal dari hemolisis eritrosit dalam sistem vaskuler. Pada hemoglobinuria semua berasal dari hemolisis eritrosit yang terdapat dalam urine, meninggalkan bungkus sel merah pada sedimen urine. Kedua bentuk tersebut urinenya berwarna merah tua sampai hamper hitam. Adj, hemoglobinurik.'),
(434, 'Halarachne ', 'Suatu genus tungau termasuk famili Halarachnidae ditemukan dalam lubang hidung singa laut dan anjing laut.'),
(435, 'Halitosis ', 'Bau tidak enak (busuk) dari pernapasan.'),
(436, 'Halitus ', 'Napas berakhir.'),
(437, 'Hannemania ', 'Tungau yang menginfestasi ambifi khususnya katak. Menyebabkan bintik merah dan vesikel.'),
(438, 'Hardpad ', 'Hiperkeratosis telapak kaki dan planum hidung, sering berhubungan dengan infeksi distemper pada anjing.'),
(439, 'Heartwater ', 'Suatu penyakit pada sapi, domba, kambing dan ungulata liar disebabkan oleh Cowdria ruminantium, ditularkan oleh caplak Amblyoma spp. Pada hewan piara menyebabkan demam dan mati mendadak, bentuk yang kurang akut dimanifestasikan dengan inkoordinasi, berputar, berjalan ngawur, konvulsi dan mati akibat ensefalitis.'),
(440, 'Heksakloroetan ', 'Suatu antelmintik digunakan untuk pengobatan fasioliasis. Manifestasi keracunan adalah narkosis, sempoyongan dan jatuh. Kasus fatal pada sapi terjadi abomasitis dan nekrosis hati.'),
(441, 'Heksakloronaftalen ', 'Suatu lubrikan tekanan tinggi mampu menyebabkan hyperkeratosis pada sapi.'),
(442, 'Helmintemesis ', 'Muntah cacing.'),
(443, 'Helmintoma ', 'Suatu tumor yang disebabkan oleh cacing parasit.'),
(444, 'Hemartros', 'Darah di dalam rongga sendi.'),
(445, 'Hematorakis ', 'Perdarahan ke dalam kanalis vertebralis.'),
(446, 'Hematorea ', 'Perdarahan berlebihan.'),
(447, 'Hematoskeokel ', 'Akumulasi darah dalam skrotum.'),
(448, 'Hematosteon ', 'Perdarahan ke dalam rongga sumsum tulang.'),
(449, 'Hematotoksik ', 'Berkaitan dengan keracunan darah atau bersifat meracun darah dan sistem hematopoietik.'),
(450, 'Hematuria ', 'Leleran darah dalam urine. Urine mungkin sedikit berdarah sangat berdarah atau berwarna coklat keruh.'),
(451, 'Hemeralopia ', 'Rabun siang, cacat penglihatan pada cahaya terang karena degenerasi atau absennya konus. Suatu cacat herediter pada Alaska malamut dan pudel mini.'),
(452, 'Hemiamiostenia ', 'Otot tanpa daya pada satu sisi tubuh.'),
(453, 'Hemianakusia ', 'Hilang pendengaran pada satu sisi tubuh.'),
(454, 'Hemidiaforesis ', 'Berkeringat pada satu sisi tubuh.'),
(455, 'Hemidistrofi ', 'Perkembangan tidak sama pada sisi tubuh.'),
(456, 'Hemiglositis ', 'Radang pada setengah bagian lidah.'),
(457, 'Hemispasmus ', 'Kejang yang hanya mengenai satu sisi.'),
(458, 'Hemofiliak ', 'Suatu hewan yang menderita hemofili.');
INSERT INTO `tbl_web_kamus` (`kamus_id`, `kamus_istilah`, `kamus_detail`) VALUES
(459, 'Hemofilosis ', 'Suatu penyakit septikemik pada sapi yang disebabkan oleh Haemophilus somnus, menciri dengan penyakit yang akut, berlangsung cepat dan tingkat kematiannya tinggi. Pada yang selamat terdapat lesi, khususnya infark merah (infark disertai perdarahan) pada otak dan sinovitis, pleuritis serta pneumonia. Tanda klinisnya sangat bervariasi, tetapi yang utama adalah kebutaan karena optalmitis, kelemahan, ataksia dan tergeletak, disebut juga meningoensefalomielitis tromboembolik.'),
(460, 'Hemofusin ', 'Suatu pigmen kuning kecoklatan yang dihasilkan oleh dekomposisi hemoglobin, memberikan warna merah gelap pada urine.'),
(461, 'Hemoglobinopati ', 'Setiap kekacauan hematologik karena perubahan struktur molekul hemoglobin yang ditentukan secara genetis, dengan abnormalitas klinik dan laboratorik yang menciri dan sering anemia. Banyak penyakit seperti itu pada manusia tetapi belum pernah terbukti pada hewan.'),
(462, 'Hemokoniasis ', 'Terdapatnya hemokonia dalam jumlah berlebihan di dalam darah.'),
(463, 'Hemokonsentrasi ', 'Meningkatnya proporsi elemen korpuskel dalam darah, sebagai akibat menurunnya kandungan zalir, misalnya, dehidrasi klinik.'),
(464, 'Hemoperfusi ', 'Melintasnya darah melalui suatu sistem adsorpsi di luar tubuh, untuk mengeluarkan senyawa yang ukuran molekulnya lebih besar daripada yang dikeluarkannya dengan hemodialisis.'),
(465, 'Hemoperikardium ', 'Suatu efusi darah ke dalam rongga pericardium disebabkan oleh robeknya atrium, perforasi ventrikel atau robeknya arteria koronaria. Lazimnya dimanifestasikan dengan kematian mendadak.'),
(466, 'Hemoperitoneum ', 'Suatu efusi darah dalam rongga peritoneum.'),
(467, 'Hemopnemokardium ', 'Efusi darah dan udara dalam perikardium.'),
(468, 'Hemopnemutoraks ', 'Suatu akumulasi darah dan udara dalam rongga pleura.'),
(469, 'Hemoragin ', 'Sitolisin dalam racun ular. Suatu endoteliotoksin yang menyebabkan kerusakan serius pada pembuluh darah.'),
(470, 'Hemositoblastoma ', 'Suatu tumor yang mengandung semua sel khas sumsum tulang.'),
(471, 'Hemositotripsis ', 'Kehancuran sel darah oleh tekanan.'),
(472, 'Hemospermia ', 'Terdapatnya darah dalam air mani.'),
(473, 'Hemostatik ', 'Mencegah aliran darah atau suatu agen yang mencegah aliran darah.'),
(474, 'Hemotoraks ', 'Akumulasi darah dalam rongga dada yang dapat menyebabkan kolaps paru dan sesak napas, absennya suara pernapasan dan mukosa pucat.'),
(475, 'Hepatodinia ', 'Nyeri pada hati.'),
(476, 'Hepatogastrik ', 'Berkaitan dengan hati dan lambung.'),
(477, 'Hepatogenus ', 'Berasal atau disebabkan oleh hati, radiografi hati atau rekaman pulsus hati.'),
(478, 'Hepatokolangitis ', 'Radang pada hati dan saluran empedu.'),
(479, 'Hepatolisis ', 'Kerusakan sel hati.'),
(480, 'Hepatolitiasis ', 'Terdapatnya batu dalam saluran empedu pada hati.'),
(481, 'Hepatomegali ', 'Pembesaran hati.'),
(482, 'Hepatopati ', 'Setiap penyakit pada hati.'),
(483, 'Hepatozoonosis ', 'Infeksi dengan protozoa Hepatozoon.'),
(484, 'Herztod ', 'Sindrom stress pada babi.'),
(485, 'Heterolisis ', 'Kerusakan sel pada suatu spesies oleh lisin dari spesies lain.'),
(486, 'Hidradenitis ', 'Radang kelenjar keringat.'),
(487, 'Hidrargiria ', 'Keracunan kronik merkuri.'),
(488, 'Hidrofobia ', 'Takut air atau rabies.'),
(489, 'Hidrokel ', 'Membengkaknya skrotum tanpa sakit disebabkan oleh pengumpulan cairan dalam tunika vaginalis buah zakar, sampai menutupnya. Disebut juga water seed.'),
(490, 'Hidropionefrosis ', 'Urine dan nanah dalam pasu ginjal.'),
(491, 'Hidropneumatosis', 'Pengumpulan cairan dan gas dalam jaringan.'),
(492, 'Hilus ', 'Suatu cekungan atau lekuk pada suatu organ, tempat masuk dan keluarnya pembuluh darah serta saraf, misalnya hilus pada ginjal, paru, indung telur, limpa.'),
(493, 'Hiostrongilosis ', 'Penyakit pada babi disebabkan oleh infestasi cacing Hyostrongylus rubidus. Menciri dengan turunnya kondisi, anemia, diare.'),
(494, 'Histolisis ', 'Kerusakan jaringan.'),
(495, 'Ikterus ', 'Kekuningan kulit, sklera, selaput lendir dan ekskresi karena hiperbilirubinemia serta deposisi pigmen empedu. Lazimnya tanda ini tampak pertama pada sklera.'),
(496, 'Infektif ', 'Dapat menghasilkan infeksi, berkaitan dengan atau menciri dengan terdapatnya patogen.'),
(497, 'Iproniasid ', 'Obat penghambat monoamin oksidase, antidepresan dan anti tuberkulosis. Takaran berlebihan pada anjing menyebabkan anemia hemolitik.'),
(498, 'Iradiasi ', 'Penyinaran dengan pancaran tenaga (panas, sinar-X, dsb) untuk tujuan penyembuhan dan diagnosa. Iradiasi makanan tertentu termasuk susu, membunuh bakteri yang berbahaya dan mencegah pembusukan. Pemotretan dengan sinar-X digunakan dalam penelitian industri dan diagnosa penyakit di dalam tubuh.'),
(499, 'Iridektropium ', 'Berputarnya iris ke arah luar (eversi).'),
(500, 'Iridemia ', 'Perdarahan iris.'),
(501, 'Iridermia ', 'Kelainan bawaan tanpa iris.'),
(502, 'Iridesen ', 'Keadaan bercahaya terang dan berubah warna.'),
(503, 'Iridesis ', 'Penempatan kembali pupil dengan memfiksasikan suatu sektor iris dalam suatu sayatan yang dibuat pada kornea atau pertemuan antara kornea dan konjungtiva.'),
(504, 'Iridin ', 'Suatu glikosida beracun yang terdapat pada daun tumbuhan, keluarga iris (Iris spp.) dan akar tumbuhan keluarga Violaceae. Menyebabkan diare hebat, sering berdarah.'),
(505, 'Iridodiastasis ', 'Suatu cacat dalam perkembangan pangkal iris, suatu koloboma iris.'),
(506, 'Iridoheksis ', 'Robeknya iris atau terenggutnya iris.'),
(507, 'Iridokapsulitis ', 'Radang iris dan selubung lensa.'),
(508, 'Iridokeratitis ', 'Radang iris dan kornea.'),
(509, 'Iridokoloboma ', 'Celah bawaan pada iris atau koloboma iris.'),
(510, 'Iridokoroiditis ', 'Radang iris dan koroid.'),
(511, 'Iridoleptinsis ', 'Penipisan atau atrofi iris.'),
(512, 'Iridomesodialisis ', 'Pembedahan untuk mengendorkan adesi di sekeliling tepi dalam iris.'),
(513, 'Iridoperifakitis ', 'Radang selubung lensa.'),
(514, 'Iridokel ', 'Burut (penonjolan) bagian iris menembus kornea.'),
(515, 'Iridosiklitis ', 'Radang iris dan badan silier.'),
(516, 'Iridosiklokoroiditis ', 'Radang iris, badan silier dan koroid.'),
(517, 'Iridosistektomi ', 'Pemotongan bagian iris untuk membentuk pupil buatan.'),
(518, 'Iridoskisis ', 'Pemisahan stroma mesodermal iris menjadi dua lapis dengan serabut lapisan bagian depan terapung pada cairan humor.'),
(519, 'Iridosklerotomi ', 'Irisan pada sklera dan tepi iris pada glaukoma.'),
(520, 'Iridosteresis ', 'Pengambilan seluruh atau sebagian dari iris.'),
(521, 'Iridoviridae ', 'Keluarga virus DNA besar dengan garis tengah 180 nm, terselubung nukleokapsid icosahedral dan berisi satu untai ganda DNA yang besar yaitu 150 kilobase. Memperbanyak diri dalam sitoplasma sel-sel yang terinfeksi. Beberapa larva serangga yang infeksi tampak berwarna-warni. Salah satu keluarga virus ini yang penting di bidang kedokteran hewan adalah virus Demam Afrika pada babi (African swine fever).'),
(522, 'Iritabel ', 'Kemampuan bereaksi terhadap suatu rangsangan atau kepekaan abnormal terhadap rangsangan.'),
(523, 'Iritabilitas ', 'Kemampuan makhluk hidup, jaringan khusus untuk bereaksi terhadap lingkungan atau respon abnormal dari tubuh terhadap rangsangan ringan, dan terlalu sensitif.'),
(524, 'Iritan ', 'Penyebab iritasi atau agen yang menyebabkan iritasi.'),
(525, 'Iritasi ', 'Perangsangan atau mudah terangsang dan terlalu peka.'),
(526, 'Iritis ', 'Radang iris. Keadaan ini bisa akut, kejadiannya tiba-tiba dengan gejala-gejala yang berat, atau kronis dengan gejala-gejala ringan tetapi berakhirnya lebih lama.'),
(527, 'Iritoektomi ', 'Pengeluaran tumpukan-tumpukan akibat katarak dengan pembedahan pada iris, bersama-sama dengan iridektomi untuk membentuk suatu pupil buatan.'),
(528, 'Isiodinia ', 'Rasa sakit pada iskium.'),
(529, 'Iskemia ', 'Kekurangan darah pada suatu bagian karena penyempitan faali atau penyumbatan pembuluh darah.'),
(530, 'Iskuria ', 'Retensi air kemih.'),
(531, 'Iskialgia ', 'Rasa sakit pada iskium.'),
(532, 'Iskiohebotomi ', 'Daerah pembedahan iskiopubik dan bagian kranial ramus pubis.'),
(533, 'Iskioneuralgia ', 'Rasa nyeri sepanjang jalur syaraf skiatik.'),
(534, 'Isoaglutinin ', 'Suatu aglutinin yang bekerja pada sel-sel individu-individu dari spesies yang sama.'),
(535, 'Isoantibodi ', 'Antibodi yang sejenis.'),
(536, 'Isodaktilisme ', 'Jejari yang panjangnya relatif sama.'),
(537, 'Isodontik ', 'Semua giginya serupa.'),
(538, 'Isodrin ', 'Suatu insektisida hidrokarbon terklorinasi, dapat menyebabkan keracunan.'),
(539, 'Isoeritrolisis ', 'Istilah kadaluwarsa untuk anemia hemolitik. Aloimun, contoh penyakit Rhesus pada manusia.'),
(540, 'Isofluorat', 'Suatu antikolinergik kuat yang dioleskan pada konjungtiva untuk mengobati glaukoma.'),
(541, 'Isoflurofat ', 'Suatu penghambat antikolinesterase yang digunakan untuk menimbulkan miosis pada glaukoma.'),
(542, 'Isoflurofosfat ', 'Suatu insektisida organofosfor yang berefek meracun, yakni sebagai antikolinesterase. Senyawa ini juga menyebabkan demielinasi saraf perifer dan medula spinalis.'),
(543, 'Isohipositosis ', 'Berkurangnya jumlah sel-sel darah putih, yang hubungannya antara jumlah berbagai bentuk normal.'),
(544, 'Isohipersitosis ', 'Bertambahnya jumlah sel-sel darah putih dengan proporsi normal sel-sel neutrofil.'),
(545, 'Isolat ', 'Memisahkan diri dari yang lain, menyisihkan, sekelompok individu yang tak dapat melakukan kawin silang sesama jenisnya lantaran kendala geografik, genetik, ekologis, dan sosial, atau suatu populasi mikroorganisme yang telah diperoleh dalam bentuk biakan murni dari kasus atau lokasi lapangan.'),
(546, 'Isolisin ', 'Suatu lisin, biasanya antibodi, yang bekerja pada sel-sel hewan dari spesies yang sama dengan spesies asal antibodi tersebut.'),
(547, 'Isoniasid ', 'Suatu senyawa antibakteri yang digunakan untuk pengobatan tuberkulosis.'),
(548, 'Isopati ', 'Pengobatan penyakit dengan produk penyakit atau dengan bahan dari organ yang terserang.'),
(549, 'Isoprenalin ', 'Suatu simpatomimetik yang digunakan sebagai bronkodilatator dan perangsang jantung. Disebut juga isoproterenol.'),
(550, 'Isorea ', 'Suatu keseimbangan antara pemasukan dan pengeluaran air dan atau zat terlarut oleh tubuh.'),
(551, 'Isoseksual ', 'Berkaitan atau menciri dengan kesamaan kelamin.'),
(552, 'Isoseluler ', 'Sel-sel yang susunannya sama (identik).'),
(553, 'Isositosis ', 'Ukuran sel yang sama, terutama sel-sel darah merah.'),
(554, 'Isosmotik ', 'Mempunyai tekanan osmotik yang sama.'),
(555, 'Isotonik ', 'Dengan tensi yang sama atau berarti suatu larutan yang bila sel tubuh direndam di dalamnya tidak ada selisih aliran air melewati selaput semipermiabel sel, juga berarti suatu larutan yang punya tonisitas yang sama dengan tonisitas larutan yang dibandingkan dengannya.'),
(556, 'Isu ', 'Suatu leleran nanah, darah atau zat lain, lesi bernanah mengeluarkan leleran.'),
(557, 'Jack ', 'Kedelai jantan, disebut stalion di Inggris.'),
(558, 'Jafarabadi ', 'Suatu jenis kerbau India untuk penghasilan susu, lazimnya berwarna hitam dengan tanduk besar menggantung.'),
(559, 'Jago', 'Burung jantan.'),
(560, 'Jagoan ', 'Ayam jantan muda piaraan, lebih tua dari empat minggu, sampai dengan dewasa kelamin kira-kira lima bulan.'),
(561, 'Jaguar ', 'Macan tutul Dunia Baru. Kucing yang sangat besar berwarna kuning dengan tutul hitam, empat atau lima tutul yang tersusun sekeliling suatu pusat dan membentuk roset, pandai berenang dan gemar memangsa, bahkan manusia. Disebut juga Panthera oncad.'),
(562, 'Jaguarundi ', 'Kucing liar Amerika, bertubuh kecil, panjang satu meter, ramping, liberang-berang, berwarna kelabu atau merah. Disebut juga Panthera jaguarundi.'),
(563, 'Jakal ', 'Anggota famili Canidae, yang berukuran dan kebiasaannya berada di tengah-tengah antara rubah dan serigala. Ramping, langkahnya panjang dengan moncong yang runcing mempunyai suara gonggongan yang tidak enak, bergerombol dan berburu pada malam hari. Merupakan predator dan pemakan bangkai. Dimasukkan dalam Canis aureus, serigala dari Timur, dan C. mesomelas, serigala berpunggung hitam.'),
(564, 'Jakobin ', 'Suatu alkaloid pirolizidin ditemukan pada gulma Senecio jacobea.'),
(565, 'Jalap ', 'Akar Exoganium purge yang dikeringkan. Suatu pencahar keras yang sejak lama sudah dihapus dari halaman buku Farmakologi.'),
(566, 'Jamse ', 'Serabut konduksi yang bermula dari traktus internodus atrium dan melewati ventrikel tapi melangkahi nodus AV. Disebut juga konduksi asesori James.'),
(567, 'Jamshidi ', 'Suatu jarum berbentuk pipa, ujungnya berbentuk kerucut dengan tepi yang tajam digunakan untuk mendapatkan bagian tengah sumsum tulang.'),
(568, 'Jamur ', 'Sekelompok infeksi fungi pada lembaga biji. Biji yang terinfeksi dimakan ternak dapat keracunan.'),
(569, 'Janet ', 'Bagal betina.'),
(570, 'Jangsionopati ', 'Suatu kekacauan pada sambungan neuromuskuler, misalnya pada botulismus, tik paralis, miastenia gravis.'),
(571, 'Janiseps ', 'Suatu monster ganda dengan satu kepala dan dua wajah yang bertolak belakang.'),
(572, 'Jansen ', 'Suatu rektaktor yang dipegangi tangan, digunakan pada pembedahan lambung.'),
(573, 'Jantan ', 'Suatu kelamin individu yang menghasilkan spermatozoa.'),
(574, 'Jejunektomi ', 'Eksisi jejunum.'),
(575, 'Jejunitis ', 'Radang jejunum.'),
(576, 'Jejunoileitis ', 'Radang jejunum dan ileum.'),
(577, 'Jejunoileal ', 'Berkaitan dengan jejunum dan ileum, menghubungkan bagian proksimal jejunum dengan bagian distal ileum.'),
(578, 'Jejunoileostomi ', 'Penciptaan anastomosis secara bedah antara jejunum proksimal dan ileum terminal, anastomosis jejunum ke ileum.'),
(579, 'Jejunostomi ', 'Anastomosis secara bedah antara dua bagian jejunum.'),
(580, 'Jejunokolostomi ', 'Anastomosis jejunum ke kolon.'),
(581, 'Jejunorafi ', 'Pembetulan jejunum dengan pembedahan.'),
(582, 'Jejunosekostomi ', 'Anastomosis jejunum ke sekum.'),
(583, 'Jejunotomi ', 'Insisi pada jejunum.'),
(584, 'Jejunum ', 'Bagian dari usus halus yang membentang dari duodenum sampai ileum.'),
(585, 'Jembrana ', 'Suatu penyakit menular yang sangat fatal, umumnya pada sapi Bali yang menciri dengan diare, perdarahan, anemia dan limfadenopati, serta demam tinggi. Penyebabnya mungkin dibawa oleh caplak Boophilus microplus.'),
(586, 'Jenet ', 'Jeni.'),
(587, 'Jeni ', 'Kedelai betina, disebut juga jenet, disebut kuda betina di Britania raya. Permukaan dinding perut.'),
(588, 'Jerboa ', 'Rodensia kecil, berekor panjang dengan kaki belakang yang panjang sesuai untuk meloncat. Terdapat banyak spesies, termasuk jarboa Mesir, Jaculus jaculus.'),
(589, 'Jersey ', 'Suatu ras sapi perah berwarna coklat kekuningan, merah keunguan atau kelabu, sering dengan pigmentasi hitam pada kulitnya.'),
(590, 'Jersian ', 'Suatu jenis sapi hasil persilangan antara pejantan Jersey dengan sapi Frisian betina. Disebut juga hibrida F-J.'),
(591, 'Jervin ', 'Substansi beracun yang terkandung dalam akar veratrum album.'),
(592, 'Jeyes ', 'Suatu larutan kresol lirsabun yang digunakan sebagai disinfektan.'),
(593, 'Jibing ', 'Suatu sifat buruk kuda yang menolak bergerak maju dan mungkin berlari mundur bila digunakan. Disebut juga balking (mogok).'),
(594, 'Jiger ', 'Lihat TUNGA PENETRANS. Disebut juga chigoe atau kutu jiger.'),
(595, 'Jird ', 'Suatu rodensia kecil sekerabat dengan GERBIL. Disebut juga Meriones spp.'),
(596, 'Jirvin ', 'Salah satu alkaloid beracun yang terdapat pada gulma Veratrum spp.'),
(597, 'Johnin ', 'Suatu ekstrak dari biakan Mycobacterium paratuberculosis digunakan pada suatu uji hipersensivitas kulit tertunda untuk penyakit Johne. Uji dianggap positif bila terjadi penebalan dan edema pada tempat suntikan setelah 48 jam.'),
(598, 'Jukstaglomeruler ', 'Dekat atau bergandengan dengan suatu glomerulus ginjal.'),
(599, 'Jukstapilorik ', 'Dekat pilorus.'),
(600, 'Jukstaspinal ', 'Dekat tulang belakang.'),
(601, 'Jukstavesikal ', 'Terletak dekat atau bergandengan dengan kandung kemih.'),
(602, 'Kabertiasis ', 'Infestasi cacing Chabertia ovina. Menciri dengan penurunan berat badan dan pengeluaran tinja yang lunak, mengandung banyak mukus. Disebut juga kabertiosis.'),
(603, 'Kakomelia ', 'Cacat suatu anggota gerak sejak lahir.'),
(604, 'Kalisefalus ', 'Suatu genus cacing kait yang terdapat pada ular.'),
(605, 'Kalkaneoapofisitis ', 'Peradangan pada bagian posterior kalkaneus, ditandai dengan nyeri dan pembengkakan.'),
(606, 'Kalkariuria ', 'Adanya garam kapur (kalsium) dalam kemih.'),
(607, 'Kalor ', 'Panas, salah satu gejala utama radang.'),
(608, 'Kalsemia ', 'Kalsium yang berlebihan dalam darah, hiperkalsemia.'),
(609, 'Kankeremia ', 'Adanya sel kanker dalam darah.'),
(610, 'Kapilaritis ', 'Radang kapiler.'),
(611, 'Kapsulitis ', 'Radang suatu kapsula, misalnya pada kapsula lensa.'),
(612, 'Kapsuloma ', 'Suatu tumor pada kapsula atau subkapsula ginjal.'),
(613, 'Karbolisme ', 'Keracunan FENOL (asam karbolat).'),
(614, 'Kardinal ', 'Tanda-tanda klinis yang paling penting suhu, kecepatan denyut jantung, kecepatan pernapasan.'),
(615, 'Kardioaktif ', 'Mempunyai efek pada jantung.'),
(616, 'Kardiodiafragmatik ', 'Berkaitan dengan jantung dan diafragma.'),
(617, 'Kardiodinia ', 'Rasa nyeri pada jantung.'),
(618, 'Kardiomegali ', 'Pembesaran jantung.'),
(619, 'Kardioperikarditis ', 'Peradangan pada jantung dan perikardium.'),
(620, 'Kardioptosis ', 'Penurunan posisi jantung.'),
(621, 'Kardiotoksik ', 'Mempunyai efek meracuni atau merusak jantung.'),
(622, 'Karditis ', 'Radang jantung.'),
(623, 'Kariopiknosis ', 'Penyusutan suatu inti sel, disertai kondensasi kromatin.'),
(624, 'Karioreksis ', 'Pecahnya inti sel disertai disintegrasi kromatinnya menjadi granulanir bentuk yang kemudian dikeluarkan dari sel.'),
(625, 'Karotenodermia ', 'Warna kekuningan pada kulit karena karotenemia.'),
(626, 'Karpitis ', 'Peradangan umum yang akut atau kronik pada selubung sendi karpal dan struktur terkait pada paha. Terjadi nyeri dan pembengkakan dan mungkin eksostose pada kejadian yang kronik.'),
(627, 'Karsinomatosis ', 'Kondisi kanker yang menyebar luas ke seluruh tubuh. Kadang-kadang digunakan untuk menyatakan penyebaran tumor yang melibatkan satu organ.'),
(628, 'Kartagener ', 'Suatu sindrom herediter pada manusia terdiri atas dekstokardia, bronkiektasis dan sinusitis. Kasus penyakit serupa dilaporkan pada anjing. Penyakit ini utamanya suatu cacat pada gerakan silia.'),
(629, 'Kaseus limfadenitis ', 'Penyakit kronik pada kambing dan domba yang disebabkan oleh bakteria Corynebacterium pseudotuberculosis dan menciri dengan pembentukan abses yang kaseopurulen (seperti keju cair) pada kelenjar limfe. Penyakit ini hanya berefek kecil pada kesehatan umum.'),
(630, 'Kaskado ', 'Penyakit kulit pada sapi yang disebabkan oleh cacing Stephanofilaria.'),
(631, 'Katabiosis ', 'Penurunan fungsi kemampuan sel secara alami.'),
(632, 'Katar ', 'Radang selaput lendir, dengan leleran bebas, terutama radang saluran udara di kepala dan tenggorokan.'),
(633, 'Katarh ', 'Peradangan pada membrana mukosa (terutama di kepala dan tenggorokan) dengan leleran bebas.'),
(634, 'Keilitis ', 'Radang bibir.'),
(635, 'Keilognatopalatoskisis ', 'Celah atau sumbing pada bibir, rahang atas, dan bagian langit-langit keras dan lunak.'),
(636, 'Keilognatoskisis ', 'Celah atau sumbing pada bibir dan rahang atas.'),
(637, 'Keilosis ', 'Bibir sumbing.'),
(638, 'Kemosensitif ', 'Sensitif terhadap perubahan komposisi bahan kimia.'),
(639, 'Kennel ', 'Suatu penyakit pernafasan akut pada anjing, lumrahnya terdiri atas laringitis, trakeitis dan bronkitis. Dapat disebabkan oleh salah satu atau kombinasi beberapa virus, bakteri dan mikoplasma. Virus parainfluenza anjing, adenovirus anjing tipe 1 dan 2, herpesvirus anjing dan Bordetella bronchiseptika adalah agen infeksiosa yang lumrah. Anjing yang terserang lazimnya batuk kering, parau dan adakalanya demam, keluar leleran serosa hidung dan limfadenopati. Terutama pada kirik, kadang penyakitnya lebih berat. Disebut juga trakeobronkitis infeksiosa, rinotrakeitis.'),
(640, 'Keratektasia ', 'Menonjolnya kornea tipis, berparut.'),
(641, 'Keratin ', 'Suatu skleroprotein yang merupakan komponen utama epidermis, rambut, kuku, jaringan menanduk dan matriks organik dari email gigi. Larutannya kadang digunakan untuk penyalut pil bila diinginkan pil melewati lambung tanpa perubahan.'),
(642, 'Keratitis ', 'Radang kornea. Keratitis dapat jeluk, jika infeksi yang menyebabkannya terbawa darah atau menyebar ke kornea dari bagian lain mata atau secara superfisial, disebabkan oleh infeksi bakteri atau virus, trauma atau reaksi alergik. Gejala klinisnya meliputi nyeri, blefarospasmus, leleran pada mata dan bila kronik, pigmentasi.'),
(643, 'Keratoglobus ', 'Suatu anomali yang menyebabkan kornea membesar dan berbentuk globular.'),
(644, 'Keratoiritis ', 'Radang kornea dan iris, korneoiritis.'),
(645, 'Keratokonjungtivitis ', 'Radang kornea dan konjungtiva.'),
(646, 'Keratokonus ', 'Penonjolan mengerucut pada bagian tengah kornea.'),
(647, 'Keratolisis ', 'Mengendurnya atau memisahnya lapisan epidermis yang menanduk.'),
(648, 'Keratomalasia ', 'Menjadi lunak dan nekrosisnya kornea sehubungan dengan kekurangan vitamin A.'),
(649, 'Keratomikosis ', 'Penyakit fungi pada kornea dan lazimnya konjungtiva. Terutama terjadi pada kuda, dimasukkan oleh benda asing.'),
(650, 'Keratopati ', 'Suatu penyakit kornea yang tidak menimbulkan radang.'),
(651, 'Keratoskleritis ', 'Radang kornea dan sklera.'),
(652, 'Kerdil ', 'Suatu hewan atau tumbuhan secara abnormal di bawah ukuran.'),
(653, 'Ketombe ', 'Keadaan kepala penuh dengan sisik-sisik seperti kulit padi. Nama lain pitiriasis.'),
(654, 'Kifosis ', 'Peningkatan kecembungan secara abnormal pada lengkungan tulang punggung bagian dada dilihat dari samping.'),
(655, 'Kilopneumotoraks ', 'Adanya cairan kil dan udara yang merembes ke dalam rongga pleura.'),
(656, 'Kilotoraks ', 'Adanya efusi kil di dalam rongga pleura. Terjadi paling lumrah pada anjing dan kucing, disebabkan oleh luka trauma pada duktus toraksikus, neoplasma dalam mediastenum kranial, atau abnormalitas saluran sejak lahir.'),
(657, 'Kiluria ', 'Adanya kil dalam urin, memberikan rupa seperti susu, karena sumbatan aliran limfe, yang menyebabkan pecahnya pembuluh limfe masuk ke dalam pelvis ginjal, uterus, kandung kemih atau uretra.'),
(658, 'Kinesalgia ', 'Rasa sakit pada penggunaan otot.'),
(659, 'Kinesia ', 'Mabuk perjalanan.'),
(660, 'Kinesialgia ', 'Rasa sakit pada penggunaan otot (kinealgia).'),
(661, 'Klastogenik ', 'Menimbulkan atau menyebabkan kerusakan atau perpecahan, seperti pada kromosom.'),
(662, 'Labioglosolaringeal ', 'Berkaitan dengan bibir, lidah dan larinks.'),
(663, 'Labiomental ', 'Berkaitan dengan bibir dan dagu.'),
(664, 'Labionasal ', 'Berkaitan dengan bibir dan hidung.'),
(665, 'Labiopalatin ', 'Berkaitan dengan bibir dan tekak.'),
(666, 'Labirintin ', 'Berkaitan dengan atau berasal dari suatu labirin.'),
(667, 'Labirintitis ', 'Radang labirin, otitis interna.'),
(668, 'Lagoftalmos ', 'Penutupan tak sempurna atau cacat dari bola mata.'),
(669, 'Lakrimatorik ', 'Menyebabkan suatu pengaliran air mata.'),
(670, 'Laktasidemi ', 'Asam laktat yang berlebihan dalam darah, laktikemi, asidemi laktat.'),
(671, 'Laktasiduria ', 'Asam laktat dalam urine.'),
(672, 'Laktigenus ', 'Menghasilkan susu.'),
(673, 'Laktikemi ', 'Asam laktat berlebihan dalam darah.'),
(674, 'Laktorea ', 'Pengaliran susu yang berlebihan dan spontan, sekresi kanjang susu meskipun sedang tidak menyusui, galaktorea.'),
(675, 'Laktoterapi ', 'Pengobatan dengan diet susu.'),
(676, 'Lamela ', 'Suatu sisik, pelat tipis, seperti pada tulang atau suatu piringan, tablet yang mengandung obat yang disisipkan di bawah bola mata.'),
(677, 'Laminitis ', 'Suatu penyakit pada kuda, dan jarang pada sapi, menciri dengan rusaknya lamina sensitif tracak, dan klinis pincang berat, terutama tracak depan. Rasa panas dan sakit pada bagian mahkotanya dan pada kasus jelek penonjolan keluar pada falanks ketiga sampai pada tapak tracak. Banyak kasus disebabkan oleh toksaemia berat, seperti pada metritis pada kuda betina, dan disebut laminitis metabolik. Beberapa disebabkan oleh trauma, seperti pada mengais-ngais karena bosan atau pada kuda yang dibebani berlebihan jarak jauh tanpa istirahat, dan disebut laminitis traumatik. Disebut juga founder.'),
(678, 'Lampas ', 'Radang pada mukosa langit-langit keras, posterior gigi seri atas. Ini dapat berhubungan dengan mastikasi dan prehensi.'),
(679, 'Lamziekte ', 'Sakit pincang, menyebabkan sapi pincang dan pertumbuhan terhambat. Disebabkan oleh defisiensi zat gizi fosfor dan menciri dengan osteofogia, kepincangan dan rakitis atau osteomalasia.'),
(680, 'Laringalgia ', 'Rasa sakit pada larinks.'),
(681, 'Laringektomi ', 'Pembuangan sebagian atau total larinks dengan pembedahan.'),
(682, 'Laringemfraksis ', 'Penyumbatan atau penutupan larinks.'),
(683, 'Laringitis ', 'Radang selaput lendir larinks, menciri dengan batuk, rasa sakit pada palpasi daerah larinks, disfagia, dan kemungkinan regurgitasi lewat hidung, lazimnya disertai tanda keradangan pada saluran pernafasan bagian atas.'),
(684, 'Laringofaringitis ', 'Radang larinks dan farinks.'),
(685, 'Laringoxserosis ', 'Kekeringan larinks.'),
(686, 'Laringoplegia ', 'Kelumpuhan larinks.'),
(687, 'Laringostenosis ', 'Penyempitan larinks.'),
(688, 'Laringotrakeitis ', 'Radang larinks dan trakea.'),
(689, 'Larinks ', 'Struktur otot dan rawan tulang, tertutup selaput lendir, terletak pada ujung kranial trakea dan disamping akar lidah dan tulang indah. Larinks mengandung pita suara, dan bertanggung jawab dalam hal suara. Merupakan bagian dari sistem pernapasan dan udara yang lewat larinks sebagai perjalanan dari farinks ke trakea dan kembali lagi dengan cara yang sama ke dan dari paru. Larinks tersusun dari Sembilan tulang rawan (tiroid, krikoid, dan epiglottis, dan pasangan arytenoid, kornikulat dan kuneiform) yang bersama-sama diikat oleh otot dan ligament.'),
(690, 'Larva ', 'Suatu tingkatan belum matang, independen, dalam daur hidup suatu hewan atau serangga, yang nyata sekali tak seperti induknya dan harus melalui perubahan bentuk dan ukuran untuk mencapai tingkat dewasa.'),
(691, 'Larval ', 'Berkaitan dengan larva atau terselubung, tersembunyi, dikatakan suatu penyakit dan suatu gejala klinis suatu penyakit.'),
(692, 'Larvat ', 'Terselubung, tersembunyi, dikatakan suatu penyakit atau suatu gejala klinis suatu penyakit.'),
(693, 'Larvisid ', 'Suatu agen yang membunuh larva serangga.'),
(694, 'Laserasi ', 'Sobek atau suatu luka dihasilkan oleh sobeknya jaringan tubuh, berbeda dengan terpotong dan insisi.'),
(695, 'Leiomioma ', 'Suatu tumor jinak otot polos, lebih sering pada uterus (leiomioma uteri) tetapi dapat terjadi pada kandung kemih, usus bagian atas dan kerongkongan.'),
(696, 'Lentikonus ', 'Suatu penonjolan berbentuk kerucut sejak lahir, anterior atau posterior lensa mata.'),
(697, 'Lepra ', 'Suatu penyakit pada manusia disebabkan Mycobacterium leprae dan menunjukkan lesi granulomatosa saraf perifer, kulit dan selaput lendir.'),
(698, 'Leptopelik ', 'Punya pelvis yang sempit.'),
(699, 'Leptospiremia ', 'Adanya leptospira dalam darah.'),
(700, 'Leptospiruria ', 'Adanya leptospira dalam urine.'),
(701, 'Leukemia ', 'Suatu penyakit progresif, ganas dari organ pembentuk darah, ditandai penyimpangan proliferasi dan perkembangan leukosit dan zat bakalnya dalam darah dan sumsum tulang. Tandanya meliputi demam dan pembesaran node limfe, limpa dan hati. Limfositosis kanjang yang terjadi pada beberapa sapi merupakan suatu respon terhadap virus leukosis viral sapi. Demikian pula leukemia dapat terjadi pada penyakit limfoproliferasi dan mieloproliferatif disebabkan oleh virus leukemia kucing pada kucing.'),
(702, 'Leukoensefalitis ', 'Peradangan pada substansi putih otak.'),
(703, 'Leukoma ', 'Pada pandangan pertama mata tampak putih karena kornea yang putih opak, mungkin disebabkan oleh radang kornea dan tukak kornea.'),
(704, 'Leukomielitis ', 'Radang pada substansi putih dari medula spinalis.'),
(705, 'Leukomielopati ', 'Penyakit pada substansi putih dari medulla spinalis.'),
(706, 'Leukopeni ', 'Penurunan jumlah leukosit dalam darah. Suatu manifestasi lazim dari sejumlah penyakit, khususnya yang disebabkan oleh virus, dengan suatu lesi radang berat yang mengalirkan sejumlah besar leukosit, suatu leukopenia konsumtif dan oleh toksin yang menekan fungsi sumsum tulang.'),
(707, 'Leukosit ', 'Suatu butir darah tak berwarna mampu bergerak seperti amuba, yang fungsi utamanya mencegah tubuh terhadap mikroorganisme penyebab sakit dan terdiri dari granulosit (basofil, eosinofil, neutrofil, nongranulosit, limfosit, monosit) dan trombosit.'),
(708, 'Leukositotoksin ', 'Suatu toksin yang merusak leukosit.'),
(709, 'Leukositozoonosis ', 'Suatu penyakit akut pada burung muda yang disebabkan oleh infeksi Leucocytozoon spp. Dan tersirat dengan emasiasi, kelemahan dan debilitas. Kematian terjadi dalam beberapa hari, dan tingkat mortalitas dapat amat tinggi. Pada burung dewasa penyakit berjalan lebih lama dan gejalanya dapat mencakup sesak nafas.'),
(710, 'Ligan ', 'Suatu molekul organik yang mendonasi elektron yang diperlukan untuk membentuk koordinat ikatan kovalen dengan ion logam. Juga suatu ion atau molekul yang bereaksi untuk membentuk suatu kompleks dengan molekul lain.'),
(711, 'Limfangitis ', 'Radang atau pembuluh limfe. Lumrah dijumpai pada hewan sakit dan sangat penting pada kuda karena dibutuhkan untuk membedakan penyebabnya dari maleus, dan pada sapi karena lesi pada tuberculosis sapi.'),
(712, 'Limpa ', 'Suatu organ limfoid besar, biasanya terletak di bagian kranial rongga perut, pada kiri lambung. Limpa berisi kumpulan penyakit sel retikuloendotelium tubuh. Pada ruminansia, limpa berlokasi di lateral kiri dinding retikulum di bawah rusuk kiri dua yang terakhir. Disebut juga lien.'),
(713, 'Linguitis ', 'Radang lidah, nama lazimnya glositis.'),
(714, 'Linkomisin ', 'Suatu antibiotik yang dihasilkan oleh Streptomyces lincolnensis, digunakan dengan garam hidroklorid pada infeksi koksi gram positif dan basilus gram negatif.'),
(715, 'Linkosamid ', 'Antibiotik bakteriostatik yang menghambat sintesis protein, termasuk linkomisin dan klindamisin.'),
(716, 'Lipasidemi ', 'Asam lemak yang berlebihan dalam darah.'),
(717, 'Lipoartritis ', 'Radang jaringan lemak suatu sendi.'),
(718, 'Liposarkoma ', 'Suatu tumor ganas menciri dengan lipoblas anaplastik besar, kadang-kadang foki sel lemak normal.'),
(719, 'Litemia ', 'Asam urat yang berlebihan dalam darah.'),
(720, 'Litofon ', 'Suatu piranti untuk deteksi kalkuli dalam kandung kemih dengan suara'),
(721, 'Lokia ', 'Suatu leleran vagina yang terjadi selama minggu pertama dan kedua setelah melahirkan.'),
(722, 'Macroptalmi ', 'Pembesaran abnormal bola mata.'),
(723, 'Mafenid ', 'Suatu sulfonamid yang digunakan sebagai obat oles untuk infeksi pada permukaan.'),
(724, 'Makromasti ', 'Ukuran berlebihan dari ambing.'),
(725, 'Makromelia ', 'Pembesaran satu atau lebih anggota badan.'),
(726, 'Malasia ', 'Kelemahan tak wajar atau pelembekan suatu bagian atau jaringan, juga digunakan sebagai kata istilah seperti osteomalasia, ensefalomalasia, mielomalasia.'),
(727, 'Maldigesti ', 'Digesti tak sempurna seperti yang terjadi pada kekurangan getah pankreas atau garam empedu. Pada ruminansia suatu gangguan mikroflora rumen dapat berefek sama.'),
(728, 'Malformasi ', 'Kelainan atau pembentukan abnormal, salah bentuk, penyimpangan anatomis, khususnya yang diperoleh selama perkembangan.'),
(729, 'Malignan ', 'Cenderung makin memburuk dan diakhiri dengan kematian, bersifat anaplasia, menyerang dan metastasis, disebut tumor.'),
(730, 'Malkosteon ', 'Kelemahan tulang, osteomalasia.'),
(731, 'Mamalgia ', 'Rasa sakit pada kelenjar susu.'),
(732, 'Mamer ', 'Berkaitan dengan kelenjar susu.'),
(733, 'Mamoplasia ', 'Perkembangan jaringan susu.'),
(734, 'Mamoplasti ', 'Bedah plastik payudara.'),
(735, 'Mamotropik ', 'Berefek memacu kelenjar susu.'),
(736, 'Masitis ', 'Radang kelenjar susu. Meskipun dapat disebabkan oleh agen kimia atau fisik, penyebabnya hampir sepenuhnya infeksius dan kebanyakan bakterial. Tanda klinis bervariasi menurut beratnya penyakit, tapi meliputi rasa nyeri, panas, bengkak pada kuartir atau setengah ambing dan susu abnormal baik berupa gumpalan atau jonjot dan encer dari fase cair. Mestitis subklinis adalah bentuk terpenting dan didiagnosis berdasarkan pemeriksaan bakteriologis atau dengan uji tak langsung berdasarkan jumlah sel dalam susu. Disebut juga mamitis, garget.'),
(737, 'Medulobastoma ', 'Suatu tumor otak yang tersusun dari meduloblas.'),
(738, 'Megalgia ', 'Rasa sakit yang sangat.'),
(739, 'Megalogastria ', 'Pembesaran lambung.'),
(740, 'Megalohepatia ', 'Pembesaran hati, hepatomegali.'),
(741, 'Megalokardia ', 'Kardiomegali, pembesaran jantung.'),
(742, 'Megalomelia ', 'Pembesaran abnormal anggota badan.'),
(743, 'Megalosefali ', 'Pembesaran abnormal dari kepala.'),
(744, 'Megalosistis ', 'Pembesaran abnormal kandung kemih.'),
(745, 'Melanoma ', 'Suatu tumor yang timbul dari melanosit, sel dendritik asal neuroektodermal, atau melanoblas. Lumrah pada kulit, mata dan rongga mulut anjing dan kuda abu-abu, tapi ada kalanya terjadi sebagai lesi bawaan sejak lahir pada babi, kambing dan sapi.'),
(746, 'Memar ', 'Perubahan warna pada jaringan superfisial akibat adanya perdarahan dari pembuluh darah di bawah kulit yang pecah masuk ke dalam jaringan, tidak ada kerusakan pada kulit. Disebut juga kontusi.'),
(747, 'Memberongot ', 'Penggunaan api sekilas untuk menghilangkan sisa bulu yang tak tercabut secara estetis pada karkas unggas dan sapi.'),
(748, 'Memurnikan ', 'Memurnikan atau membebaskan dari bahan asing.'),
(749, 'Menacanthus ', 'Suatu genus kutu dalam superfamili Amblycera.'),
(750, 'Menadion ', 'Suatu derivate vitamin K digunakan pada defisiensi vitamin ini. Larut lemak dan dapat digunakan lewat mulut. Disebut juga vitamin K3.'),
(751, 'Mencederai ', 'Mencederai, merusak.'),
(752, 'Mencret ', 'Menciri dengan diare.'),
(753, 'Mendengkur ', 'Suatu suara tanpa sengaja yang dalam, keluar dari farinks dan tekak lunak pada inspirasi atau ekspirasi, sering intermiten tergantung posisi kepala. Dapat merupakan petunjuk suatu lesi obstruksi farinks yang kronik.'),
(754, 'Mendesentisisasikan ', 'Menghilangkan sensai atau hal desentisasi.'),
(755, 'Mengepiteliasi ', 'Menutup dengan epitelium.'),
(756, 'Mengesterifikasikan ', 'Pengikatan dengan suatu alkohol dengan mengeliminasi suatu molekul air, pembentukkan suatu ester.'),
(757, 'Mengetanatisasikan ', 'Melakukan etanasi.'),
(758, 'Mengkatalisis ', 'Menyebabkan atau menghasilkan katalisis.'),
(759, 'Mengucilkan ', 'Pemisah atau memisah secara abnormal suatu bagian kecil dari keseluruhan.'),
(760, 'Mengurutkan ', 'Dalam istilah komputer untuk menyusun data kembali, misalnya ke dalam perintah alfabetik atau numerik.'),
(761, 'Meninga ', 'Tiga selaput yang menutupi otak dan medula spinalis, dura mater, arachnoid dan piamater. Terdiri atas selaput kranial dan spinal dan bagian khusus selaput pada sel diafragma, falx serebri dan tentorium serebelli.'),
(762, 'Meningitis ', 'Radang selaput otak. Bila proses radang merusak durameter penyakit ini disebut pachymeningitis, bila arachmoid dan piameter dilibatkan disebut leptomeningitis atau meningitis sejati. Diklasifikasikan juga sebagai fibronosa, hemoragik, purulen atau eosinofilik, tergantung reaksi utama jaringan. Istilah meningitis tidak mengarah ke suatu penyakit khas yang sungguh tapi agaknya ke kondisi patologis peradangan jaringan selaput otak. Agen penyebabnya dapat setiap hal yang mengaktifkan proses peradangan, meliputi organisme patogenik dan nonpatogenik, seperti bakteri, virus, dan jamur, toksin kimiawi seperti timah, arsen, media kontras yang digunakan pada mielografi, dan sel malignan metastatik. Pada hewan tak ada meningitis khas, banyak kejadian meningitis sebagai komplikasi sekunder penyakit lain dan disebabkan oleh bakteri. Tanda klinis yang lumrah tersifat meliputi demam, hiperestesia kutaneus dan kekakuan otot leher dan anggota badan depan. Suatu pemeriksaan cairan serebrospinal akan membantu diagnosis.'),
(763, 'Meningoensefalomielitis ', 'Radang selaput otak, otak dan medula spinalis.'),
(764, 'Meningomielitis ', 'Radang medula spinalis dan salaputnya.'),
(765, 'Meningoserebritis ', 'Radang otak dan selaputnya.'),
(766, 'Meningorafi ', 'Jahit bedah selaput, khususnya selaput otak.'),
(767, 'Menstimulasi ', 'Membangkitkan aktivitas fungsional pada suatu bagian.'),
(768, 'Mereabsorbsi ', 'Berkaitan dengan merkuri atau suatu sediaan mengandung merkuri.'),
(769, 'Mersalil ', 'Suatu diuretik merkurial, lazimnya dikombinasikan dengan teofilin pada pengobatan edema yang disebabkan oleh penyakit hati dan ginjal.'),
(770, 'Mesenkim ', 'Suatu rangkaian jaringan ikat embrional mesoderm, asal terbentuknya otot dan jaringan tubuh dan juga pembuluh darah dan limfe.'),
(771, 'Metritis ', 'Radang uterus.'),
(772, 'Mielomalasia ', 'Pelunakan tak wajar tali spinal. Tak lumrah dinyatakan suatu kenyataan terpisah dari ensefalomalasia. Keracunan oleh Phalaria tuberosa pada domba dan sorghum pada kuda merupakan dua contoh. Terdapat paresis atau kelumpuhan yang bertahap.'),
(773, 'Migrasi ', 'Keluarnya leukosit melalui dinding pembuluh darah, diapedesis.'),
(774, 'Mikosis ', 'Setiap penyakit yang disebabkan oleh jamur (fungi).'),
(775, 'Mikroba ', 'Suatu mikroorganisme, khususnya suatu bakteri patogenik.'),
(776, 'Mikrobisidal ', 'Perusakan jasad renik.'),
(777, 'Mikrofag ', 'Suatu fagosit kecil, suatu leukosit neutrofilik yang bergerak aktif mampu fagositosis.'),
(778, 'Misetoma ', 'Infeksi granulomatous kronik pada kulit dan jaringan di bawahnya dengan edema, rongga kosong dan berbutir, memberi kesan sebagai suatu tumor. Dapat disebabkan oleh bakteri (aktinomisetoma) atau jamur (emisetoma).'),
(779, 'Monodaktil ', 'Suatu kaki dengan satu jari, seperti pada kuda.'),
(780, 'Muntahan ', 'Benda-benda yang dikeluarkan dari perut melalui mulut. Pada kuda pengeluaran melalui cuping hidung dan merupakan kejadian terminal atau mengeluarkan isi perut melalui mulut.'),
(781, 'Muskarinik ', 'Berkaitan dengan transmisi impuls saraf yang diantarai reseptor muskarinik.'),
(782, 'Nasal ', 'Berkaitan dengan hidung.'),
(783, 'Nasolabial ', 'Berkaitan dengan hidung dan bibir.'),
(784, 'Nefralgia ', 'Rasa sakit pada suatu ginjal.'),
(785, 'Nefrik ', 'Berkaitan dengan ginjal.'),
(786, 'Nefritis ', 'Radang ginjal, suatu penyakit fokal, difus proliferatif atau destruktif yang dapat melibatkan glomerulus, tubulus atau jaringan interstisial ginjal.'),
(787, 'Nefroma ', 'Suatu tumor pada jaringan ginjal.'),
(788, 'Neuroretinitis ', 'Radang saraf mata dan retina.'),
(789, 'Neurosis ', 'Suatu kekacauan emosional yang dapat mengganggu kemampuan hewan untuk hidup secara normal, terkadang disebut psikoneurosis. Misalnya kuda yang selalu menggerak-gerakkan kepala dan lehernya dikandang, menggigiti wadah pakan.'),
(790, 'Neurosklerosis ', 'Pengerasan jaringan saraf.'),
(791, 'Neurotisasi ', 'Regenerasi saraf mengikuti pembelahan sel atau implantasi suatu saraf ke dalam otot yang lumpuh.'),
(792, 'Neurotoksin ', 'Suatu zat yang meracun atau merusak jaringan saraf.'),
(793, 'Neurovaksin ', 'Suatu virus vaksin, misalnya rabies, yang disiapkan dengan menumbuhkan virus dalam otak kelinci.'),
(794, 'Neurovaskular ', 'Berkaitan dengan unsur-unsur saraf dan vaskular, atau dengan saraf pengendali besarnya lumen pembuluh darah.'),
(795, 'Neutrofil ', 'Suatu leukosit bergranula dengan inti berlobus tiga sampai lima yang dihubungkan dengan benang kromatin dan sitoplasma yang mengandung granula sangat halus, disebut juga leukosit polimorfonuklear atau setiap sel, struktur, dan unsur tercat oleh pewarna netral.'),
(796, 'Ngiler ', 'Leleran saliva dari mulut, suatu gambaran normal pada beberapa jenis anjing seperti St. Bernard, New foundland dan British bulldog, diduga dikarenakan longgarnya bibir yang menggantung. Pada anjing lain pada kucing dapat menjadi suatu tanda abnormalitas penelanan, rasa sakit lesi dalam mulut, penyakit gigi atau gangguan kelenjar ludah dengan hipersialosis.'),
(797, 'Niasin ', 'Suatu vitamin yang larut dalam air, bagian dari vitamin B. kompleks, terdapat pada berbagai jaringan tumbuh-tumbuhan dan hewan. Esential dalam metabolisme normal karbohidrat. Hampir semua pakan alami hewan mengandung cukup sehingga tidak diperlukan imbuhan, kecuali untuk babi dengan diet berat jagung. Disebut juga vitamin PP, asam nikotinat. Defisiensi nutrisi n. menyebabkan diare berdarah, radang kulit, anemia, dan radang lambung yang hebat disertai tukak-tukak pada mulut dan lidah. Disebut juga pelagra.'),
(798, 'Niche ', 'Lubang atau lekukan kecil terutama pada dinding organ berongga yang cenderung menahan media kontras pada radiografi.'),
(799, 'Nicking ', 'Penyempitan lokal pada pembuluh darah retina atau membuat irisan kecil. Prosedur operasi khusus pada bagian ekor kuda sehingga menyebabkannya lebih tinggi. Dianggap suatu tindakan yang tak bermanfaat. Disebut juga miotomi ventral-kaudal dan dalam pemuliabiakan adalah fenomenon apabila anak jauh lebih unggul daripada kedua orang tuanya.'),
(800, 'Nifedipin ', 'Penyumbat jalur kalsium yang digunakan terutama sebagai vasodilatator.'),
(801, 'Nikarbasin ', 'Suatu koksidiostat yang sangkit tetapi menyebabkan bercak pada kuning telur dan menurunkan produksi telur pada ayam petelur. Pemberian yang berlebih menyebabkan inkoordinasi, lemah kurang makan dan hilang berat.'),
(802, 'Niketamida ', 'Suatu perangsang saraf pusat dan pernapasan.'),
(803, 'Niklosamida ', 'Suatu obat anti cacing yang mangkus terhadap semua cacing pita pada semua anjing kecuali Echinococcus granulosus dan Dipyllidium Caninum. Memiliki aktivitas lumayan terhadap Paramphistomum pada ruminansia.'),
(804, 'Nikotin ', 'Suatu alkaloid yang amat beracun yang dalam bentuk murni awawarna, cairan berminyak, dengan rasa yang amat tajam. Merupakan penyusunan tembakau, dapat dibuat secara sintesis.'),
(805, 'Niksis ', 'Tusukan, atau parasentesis.'),
(806, 'Niktotiflosis ', 'Rabun malam, atau niktalopania.'),
(807, 'Nimfa ', 'Suatu tahap perkembangan artropoda tertentu (misalnya, caplak, kutu, tungau) antara bentuk larva dan dewasa, wujudnya lebih mirip yang dewasa.'),
(808, 'Nimfomania ', 'Keadaan tingkah laku hewan betina yang mengalami estrus terus-menerus atau lebih lama dengan interval lebih pendek daripada normal, Palpasi rektal dapat mengungkap adanya folikel sistik atau tanpa kelainan sama sekali.'),
(809, 'Nirsakit ', 'Suatu diagnosis yang dicadangkan untuk kasus-kasus dengan kelainan yang nampak pada fungsi, tetapi penyakitnya tidak dapat dinyatakan.'),
(810, 'Nisin ', 'Suatu antibiotik yang diisolasi dari biakan streptokokus penghasil asam laktat dan terkenal memiliki aktivitas anti-bakteri terhadap bakteri-bakteri gram-positif.'),
(811, 'Nistagmus ', 'Gerakan kedua bola mata secara serempak berkala, berirama dan tak disadari. Ada komponen gerak lamban ke satu arah dan kembali dengan cepat. Gerakan dapat vertikal, horizontal atau berputar. Penyebab umumnya adalah lesi otak kecil atau aparatus vestibular, atau kenaikan tekanan intrakardial.'),
(812, 'Nitrofuran ', 'Sekumpulan senyawa antibakteri, termasuk nitrofurantoin, nitrofurason, dan lain-lain yang digunakan pada ternak karena efektivitasnya melawan bakteri gram-positif. Dapat digunakan lewat mulut untuk pengobatan massal dalam pakan maupun minuman. Lihat juga NITROFURASON, NIFUROXIME.'),
(813, 'Nitrofurantoin ', 'Suatu agen anti-bakteri derivat nitrofuran yang digunakan dalam pengobatan infeksi saluran kemih.'),
(814, 'Nitrofurazon ', 'Suatu antibiotik derivat nitrofuran yang paling sering digunakan secara olesan lewat mulut untuk pengobatan enterik. Pemberian yang terlalu banyak atau terus menerus menyebabkan paralisis atau konvulsi pada pedet.'),
(815, 'Nodul ', 'Bintik kecil, yang padat dan dapat dideteksi dengan sentuhan.'),
(816, 'Noktalbuminuria ', 'Kelebihan albumin dalam kemih yang disekresikan malam hari.'),
(817, 'Nondepolarisasi ', 'Obat antikolinergik yang berkompetisi dengan asetikolin pada reseptor kolinergik. Disebut juga obat kompetitif, misalnya d-tubokurarin, galamin pankuronium.'),
(818, 'Nondisjungsi ', 'Kegagalan, kromosom homolog berpindah dari satu ke sel yang terpisah selama pembelahan meiosis yang pertama atau kegagalan 2 kromatida sebuah kromosom berpindah ke sel yang terpisah selama mitosis dan meiosis kedua. Sebagai hasil, satu sel anak memiliki 2 kromosom atau 2 kromatida dan yang lain tidak memiliki satupun.'),
(819, 'Nonrespiratori ', 'Berasal dari tempat lain selain saluran pernafasan.'),
(820, 'Nonsteroid ', 'Kelompok obat yang memiliki aktivitas analgesik, antipiretik dan antiradang yang disebabkan oleh kemampuannya menghambat sintesis prostaglandin, termasuk aspirin, parasetamol, fenibutazon, indometasin, tolmetin, ibuprofen dan kerabatnya.'),
(821, 'Nonsupuratif ', 'Menunjuk pada radang tanpa pembentukan nanah, misalnya poliartritis nirsupuratif pada anak domba dan pedet.'),
(822, 'Normoblastosis ', 'Produksi normoblas (metarubrisit) yang berlebihan oleh sumsum tulang. Disebut juga metarubrisitosis.'),
(823, 'Nosematosis ', 'Penyakit yang disebabkan oleh infeksi NOSEMA dan ENCEPHALITOZOON CUNICULI. Parasit menginvasi sistem saraf umum dan ginjal namun tanpa gejala klinis yang tampak. Merupakan infeksi yang lumrah pada kelinci dan terkadang pada mamalia lainnya.'),
(824, 'Nosiseptor ', 'Suatu reseptor yang terangsang oleh adanya cedera, reseptor nyeri.'),
(825, 'Nosogeni ', 'Perkembangan suatu penyakit, patogenesis.'),
(826, 'Nosokomial ', 'Mengacu kepada atau berasal dari rumah sakit.'),
(827, 'Nosoparasit ', 'Suatu organisme yang terdapat dalam suatu penyakit, yang mampu memodifikasi, tetapi tidak mampu menghasilkan.'),
(828, 'Nosopoietik ', 'Menyebabkan penyakit.'),
(829, 'Notarium ', 'Struktur bertulang yang terbentuk sebagai akibat menyatunya dua atau lebih toraks vertebrata sebagaimana terjadi pada beberapa burung. Disebut juga os dorsale.'),
(830, 'Notensefalokel ', 'Tonjolan burut otak di bagian belakang kepala.'),
(831, 'Notorkorda ', 'Suatu tali silindris terdiri atas sel-sel pada bagian dorsal suatu janin, menandai sumbu longitudinalnya, sifat khas semua kortada. Merupakan pusat perkembangan rangka aksial. Pada mamalia dewasa sisanya terdapat dalam pusat yang menyerupai pulpa dan diskus antarvertebra.'),
(832, 'Novobiosin ', 'Suatu antibakteri dihasilkan Streptomyces niveus, digunakan dalam pengobatan infeksi stafilokokus dan organisme gram positif lain. Disebut juga Cathomycin, Albamycin.'),
(833, 'Nucleosoma ', 'Salah satu dari kompleks histon dan DNA dalam sel eukariotik yang dengan mikroskop elektron terlihat sebagai badan lirmanik pada seutas DNA.'),
(834, 'Nukleofil ', 'Suatu donor elektron dalam reaksi kimia yang melibatkan kualitas kovalen, di sini elektron yang diberikan mengikat gugus kimia yang lain (yang disebut elektrofil).'),
(835, 'Nukleofugal ', 'Bergerak menjauhi inti.'),
(836, 'Nukleoid ', 'Menyerupai inti, badan lirinti yang terkadang terlihat di pusat suatu eritrosit atau bahan genetik (asam nukleat) riketsia, klamidia, dan beberapa virus terletak di pusat mikroorganisme yang bersangkutan.'),
(837, 'Nukleokapsid ', 'Suatu unit minimum struktur virus, terdiri atas kapsid protein dengan asam nukleat didalamnya. Untuk beberapa virus nukleokapsid terbungkus dalam suatu selubung.'),
(838, 'Nukleopetal ', 'Bergerak menuju nukleus.'),
(839, 'Nukleotoksin ', 'Suatu racun yang berasal dari inti sel, atau racun yang mempengaruhi inti sel.'),
(840, 'Nulipara ', 'Betina yang belum menghasilkan keturunan yang hidup.'),
(841, 'Nutasi ', 'Gerakan mengangguk, terutama yang tidak disadari.'),
(842, 'Obesitas ', 'Akumulasi berlebihan lemak dalam tubuh, berat badan meningkat di atas normal bila dibandingkan dengan umur, tinggi dan struktur tulang.'),
(843, 'Obliterasi ', 'Hilangnya secara menyeluruh karena penyakit, degenarasi, operasi, radiasi, dan lain-lain.'),
(844, 'Obtanden ', 'Memiliki kemampuan menurunkan kepekaan dan meredakan nyeri atau atau agen pereda dan penyebab anastetik lokal.'),
(845, 'Oesofagostomosis ', 'Infestasi oleh cacing Oesophagostomum spp, menyebabkan nodul-nodul nekrotik pada dinding usus. Gejala klinik yang diakibatkan mencakup kondisi yang buruk dan keluarnya tinja lunak yang mengandung mukus melebihi normal.'),
(846, 'Ofidisme ', 'Keracunan oleh bisa ular. Disebut juga envenomasi lebih tepat sebagai penyuntikan racun dari pada efek racun terhadap pasien.'),
(847, 'Oftalmia ', 'Radang yang parah pada mata, konjungtiva atau bagian mata yang lebih dalam.'),
(848, 'Oftalmik ', 'Berhubungan dengan mata.'),
(849, 'Oftalmodonesis', 'Gerak gemetaran mata.'),
(850, 'Oftalmoneuritis ', 'Radang saraf mata.'),
(851, 'Oftalmopati ', 'Setiap penyakit mata.'),
(852, 'Oftalmoplasti ', 'Operasi plastik pada mata atau bagian-bagiannya.'),
(853, 'Oftalmoragia ', 'Perdarahan dari mata.'),
(854, 'Oftalmorea ', 'Keluarnya darah dari mata.'),
(855, 'Oftalmoskop ', 'Suatu perkakas untuk memeriksa bagian dalam mata. Ia mengirim sinar yang terang dan sempit menembus lensa mata dan mengandung cermin dan lensa yang berlubang-lubang sehingga dokter hewan dapat memeriksa bagian dalam mata. Sangat menolong untuk mendeteksi kelainan yang mungkin pada mata, maupun kelainan organ lain yang dicerminkan oleh keadaan mata.'),
(856, 'Oftalmoskopi ', 'Pemeriksaan mata menggunakan oftalmoskop. Pada hewan kesulitan ditemui karena diperlukan paksaan. Untuk hasil terbaik diperlukan suasana gelap, dilatasi pupil, tiadanya distraksi dan paksaan sekecil mungkin.'),
(857, 'Oftalmosteresis ', 'Kehilangan mata.'),
(858, 'Oksfendasol ', 'Sebuah obat cacing benzimidasol yang amat efisien yang digunakan secara ekstensif pada ruminansia terhadap spektrum cacing yang luas. Formulasi khusus tersedia untuk injeksi intraruminal. Juga efektif terhadap strongil kuda.'),
(859, 'Oksiblepsia ', 'Ketajaman penglihatan yang luar biasa.'),
(860, 'Oksiklosanid ', 'Suatu obat yang mujarab untuk cacing hati dewasa pada sapi perah. Punya waktu tarik pendek dan efektif juga terhadap Paramphistomum yang belum dewasa.'),
(861, 'Oksikuinolin ', 'Suatu antiseptik topikal dengan kerja antibakteri dan antijamur.'),
(862, 'Oksiput ', 'Bagian belakang tengkorak. Tonjolan oksipital luar dan krista oksipital, dapat menonjol pada beberapa janin anjing.Terkadang kena trauma dengan akibat terbentuknya hematoma.'),
(863, 'Oksisefali ', 'Kondisi kongenital berupa ujung kepala yang meruncing disebabkan oleh penutupan prematur jalinan korona dan lamdoid. Disebut juga akrosefali.'),
(864, 'Oksisinesia ', 'Nyeri pada gerakan.'),
(865, 'Oksiuriasis ', 'Penyakit yang disebabkan oleh infestasi nematoda OXYURIS. Ditandai dengan iritasi yang sangat pada daerah sekitar dubur menyebabkan penggosokan dan penggigitan ekor.');
INSERT INTO `tbl_web_kamus` (`kamus_id`, `kamus_istilah`, `kamus_detail`) VALUES
(866, 'Olekranartritis ', 'Radang sendi siku.'),
(867, 'Olekranartropati ', 'Penyakit pada sendi siku.'),
(868, 'Oligodipsi ', 'Hilangnya haus secara tak normal.'),
(869, 'Oligodonsia ', 'Tiadanya beberapa gigi secara kongenital.'),
(870, 'Oligogalaksia ', 'Kurangnya sekresi kuku.'),
(871, 'Oligohidruria ', 'Konsentrasi air kencing yang abnormal.'),
(872, 'Oligoptialisme ', 'Sekresi air ludah yang berkurang.'),
(873, 'Oliguria ', 'Pengeluaran air kencing yang berkurang setiap hari. Menjadi penting bila pemasukan air normal atau bila air tersedia tak terbatas, ini kemudian merupakan tanda insufisiensi ginjal.'),
(874, 'Olifantvel ', 'Kulit yang menebal, berlipat, mengalami alopesia oleh BESNOITIA BESNOITI pada sapi.'),
